import React, { useState, useEffect } from 'react';
import './Header.css';

const Header = ({ onSearch, onFilter, onSort }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilters, setActiveFilters] = useState([]);
  const [activeTab, setActiveTab] = useState('for-you');
  const [sortOption, setSortOption] = useState('newest');
  const [isSearchFocused, setIsSearchFocused] = useState(false);
  
  const filterOptions = [
    { id: 'ux-designer', label: 'UX Designer' },
    { id: 'ui-designer', label: 'UI Designer' },
    { id: 'product-designer', label: 'Product Designer' },
    { id: 'visual-identity', label: 'Visual Identity' }
  ];
  
  const handleFilterClick = (filterId) => {
    let newFilters;
    if (activeFilters.includes(filterId)) {
      newFilters = activeFilters.filter(id => id !== filterId);
    } else {
      newFilters = [...activeFilters, filterId];
    }
    setActiveFilters(newFilters);
    
    if (onFilter) {
      onFilter(newFilters);
    }
    
    // 更新筛选结果计数
    if (onSearch) {
      onSearch(searchTerm);
    }
  };
  
  const handleClearFilters = () => {
    setActiveFilters([]);
    setSearchTerm('');
    
    if (onSearch) {
      onSearch('');
    }
    
    if (onFilter) {
      onFilter([]);
    }
  };
  
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    
    // 实时搜索，使用防抖处理
    if (onSearch) {
      // 使用setTimeout模拟防抖，实际项目中可以使用lodash的debounce函数
      clearTimeout(window.searchTimeout);
      window.searchTimeout = setTimeout(() => {
        onSearch(value);
      }, 300); // 300ms的延迟，避免频繁触发搜索
    }
  };
  
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };
  
  const handleSortChange = (e) => {
    const value = e.target.value;
    setSortOption(value);
    
    if (onSort) {
      onSort(value);
    }
  };
  
  return (
    <header className="header">
      <div className="header-content">
        <div className={`search-container ${isSearchFocused ? 'focused' : ''}`}>
          <div className="search-input-wrapper">
            <input
              type="text"
              placeholder="Search by Category, Company or ..."
              value={searchTerm}
              onChange={handleSearchChange}
              onFocus={() => setIsSearchFocused(true)}
              onBlur={() => setIsSearchFocused(false)}
              className="search-input"
            />
            <button className="search-button">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <circle cx="11" cy="11" r="8"></circle>
                <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
              </svg>
            </button>
          </div>
          
          <div className="filter-tags">
            {filterOptions.map(filter => (
              <button
                key={filter.id}
                className={`filter-tag ${activeFilters.includes(filter.id) ? 'active' : ''}`}
                onClick={() => handleFilterClick(filter.id)}
              >
                {filter.label}
              </button>
            ))}
            
            {(activeFilters.length > 0 || searchTerm) && (
              <button className="clear-filters" onClick={handleClearFilters}>
                Clear filters
              </button>
            )}
          </div>
        </div>
        
        <div className="header-bottom">
          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'for-you' ? 'active' : ''}`}
              onClick={() => handleTabChange('for-you')}
            >
              Jobs For You
            </button>
            <button 
              className={`tab ${activeTab === 'popular' ? 'active' : ''}`}
              onClick={() => handleTabChange('popular')}
            >
              Popular
            </button>
          </div>
          
          <div className="sort-container">
            <span className="sort-label">Sort:</span>
            <select 
              className="sort-select"
              value={sortOption}
              onChange={handleSortChange}
            >
              <option value="newest">Newest</option>
              <option value="salary">Highest Salary</option>
              <option value="relevant">Most Relevant</option>
            </select>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
