  // ÊéíÂ∫èÂ∑•‰Ωú
  sortJobs(jobs, sortBy = 'date') {
    const sortedJobs = [...jobs];

    switch (sortBy) {
      case 'date':
        return sortedJobs.sort((a, b) => {
          // Êñ∞Â∑•‰Ωú‰ºòÂÖà
          if (a.isNew && !b.isNew) return -1;
          if (!a.isNew && b.isNew) return 1;

          // ÊåâÂèëÂ∏ÉÊó•ÊúüÊéíÂ∫è
          const dateOrder = ['Today', 'Yesterday', '2 days ago', '3 days ago', '1 week ago'];
          const indexA = dateOrder.indexOf(a.postedDate);
          const indexB = dateOrder.indexOf(b.postedDate);

          if (indexA !== -1 && indexB !== -1) return indexA - indexB;
          if (indexA !== -1) return -1;
          if (indexB !== -1) return 1;

          return 0;
        });

      case 'salary':
        return sortedJobs.sort((a, b) => (b.salary || 0) - (a.salary || 0));

      case 'company':
        return sortedJobs.sort((a, b) => a.company.localeCompare(b.company));

      default:
        return sortedJobs;
    }
  }

  // Ê∑ªÂä†Êï∞ÊçÆÊõ¥Êñ∞ÁõëÂê¨Âô®
  addUpdateListener(callback) {
    this.listeners.add(callback);
    return () => this.listeners.delete(callback);
  }

  // ÈÄöÁü•ÊâÄÊúâÁõëÂê¨Âô®
  notifyListeners(data) {
    this.listeners.forEach(callback => {
      try {
        callback(data);
      } catch (error) {
        console.error('ÁõëÂê¨Âô®ÂõûË∞ÉÈîôËØØ:', error);
      }
    });
  }

  // Ëé∑ÂèñÁªüËÆ°‰ø°ÊÅØ
  getStats() {
    return {
      ...this.stats,
      totalJobs: this.jobs.length,
      newJobs: this.jobs.filter(job => job.isNew).length,
      sources: this.sources,
      lastUpdate: this.lastUpdate,
      isUpdating: this.isUpdating,
      updateFrequency: this.updateFrequency
    };
  }

  // ËÆæÁΩÆÊõ¥Êñ∞È¢ëÁéá
  setUpdateFrequency(minutes) {
    this.updateFrequency = minutes * 60 * 1000;

    // Â¶ÇÊûúÊ≠£Âú®ËøêË°åÔºåÈáçÊñ∞ÂêØÂä®ÂÆöÊó∂Âô®
    if (this.updateInterval) {
      this.stopRealTimeUpdates();
      this.startRealTimeUpdates();
    }

    console.log(`‚è∞ Êõ¥Êñ∞È¢ëÁéáËÆæÁΩÆ‰∏∫ ${minutes} ÂàÜÈíü`);
  }

  // Ëé∑ÂèñÂΩìÂâçÂ∑•‰ΩúÊï∞ÊçÆ
  getCurrentJobs() {
    return {
      jobs: this.jobs,
      sources: this.sources,
      lastUpdate: this.lastUpdate,
      stats: this.getStats()
    };
  }
}

