// ÂÆûÊó∂Âà∑Êñ∞ÊúçÂä° - Á°Æ‰øùÊØèÊ¨°Âà∑Êñ∞ÈÉΩËé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
import { searchRemoteJobs, fetchJobspressoJobs, fetchWeWorkRemotelyJobs } from './jobService';
import { fetchRemoteOKJobs } from './realJobAPI';
import { config } from '../config/production';

class RefreshService {
  constructor() {
    this.isRefreshing = false;
    this.lastRefresh = null;
    this.refreshListeners = new Set();
    this.cacheBypass = true; // Âº∫Âà∂ÁªïËøáÁºìÂ≠ò
    this.refreshStats = {
      totalRefreshes: 0,
      successfulRefreshes: 0,
      failedRefreshes: 0,
      averageRefreshTime: 0
    };
  }

  // Âº∫Âà∂Âà∑Êñ∞ÊâÄÊúâÊï∞ÊçÆÊ∫ê
  async forceRefreshAllSources(searchTerm = '', filters = {}) {
    if (this.isRefreshing) {
      console.log('üîÑ Âà∑Êñ∞Ê≠£Âú®ËøõË°å‰∏≠ÔºåËØ∑Á®çÂÄô...');
      return { success: false, message: 'Âà∑Êñ∞Ê≠£Âú®ËøõË°å‰∏≠' };
    }

    this.isRefreshing = true;
    const startTime = Date.now();
    this.refreshStats.totalRefreshes++;

    try {
      console.log('üöÄ ÂºÄÂßãÂº∫Âà∂Âà∑Êñ∞ÊâÄÊúâÊï∞ÊçÆÊ∫ê...');
      
      // Ê∏ÖÈô§ÊâÄÊúâÁºìÂ≠òÔºåÁ°Æ‰øùËé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
      await this.clearAllCaches();
      
      // Âπ∂Ë°åË∞ÉÁî®ÊâÄÊúâÊï∞ÊçÆÊ∫êÔºåÊØè‰∏™ÈÉΩÂ∏¶‰∏äÊó∂Èó¥Êà≥Èò≤Ê≠¢ÁºìÂ≠ò
      const timestamp = Date.now();
      const refreshPromises = [
        this.refreshJobspresso(searchTerm, filters, timestamp),
        this.refreshRemoteOK(timestamp),
        this.refreshWeWorkRemotely(searchTerm, filters, timestamp)
      ];

      console.log('üì° Âπ∂Ë°åËØ∑Ê±ÇÊâÄÊúâÊï∞ÊçÆÊ∫ê...');
      const results = await Promise.allSettled(refreshPromises);
      
      // Â§ÑÁêÜÁªìÊûú
      let allJobs = [];
      let successfulSources = [];
      let failedSources = [];
      
      results.forEach((result, index) => {
        const sourceNames = ['Jobspresso', 'RemoteOK', 'WeWorkRemotely'];
        const sourceName = sourceNames[index];
        
        if (result.status === 'fulfilled' && result.value.success) {
          allJobs = [...allJobs, ...result.value.jobs];
          successfulSources.push(sourceName);
          console.log(`‚úÖ ${sourceName}: ${result.value.jobs.length} ‰∏™ÊúÄÊñ∞Â∑•‰Ωú`);
        } else {
          failedSources.push(sourceName);
          console.warn(`‚ùå ${sourceName} Âà∑Êñ∞Â§±Ë¥•:`, result.reason?.message || result.value?.error);
        }
      });

      // ÂéªÈáçÂíåÊéíÂ∫è
      const uniqueJobs = this.deduplicateJobs(allJobs);
      const sortedJobs = this.sortJobsByFreshness(uniqueJobs);
      
      // Ê∑ªÂä†Âà∑Êñ∞Êó∂Èó¥Êà≥
      const jobsWithRefreshInfo = sortedJobs.map(job => ({
        ...job,
        refreshedAt: new Date().toISOString(),
        isFresh: true,
        refreshId: timestamp
      }));

      const refreshTime = Date.now() - startTime;
      this.refreshStats.successfulRefreshes++;
      this.refreshStats.averageRefreshTime = 
        (this.refreshStats.averageRefreshTime + refreshTime) / 2;
      
      this.lastRefresh = new Date();

      console.log(`üéâ Âà∑Êñ∞ÂÆåÊàê! Ëé∑ÂèñÂà∞ ${jobsWithRefreshInfo.length} ‰∏™ÊúÄÊñ∞Â∑•‰Ωú`);
      console.log(`üìä ÊàêÂäüÊ∫ê: ${successfulSources.join(', ')}`);
      console.log(`‚è±Ô∏è Âà∑Êñ∞ËÄóÊó∂: ${refreshTime}ms`);

      // ÈÄöÁü•ÊâÄÊúâÁõëÂê¨Âô®
      this.notifyRefreshListeners({
        jobs: jobsWithRefreshInfo,
        sources: successfulSources,
        failedSources,
        refreshTime,
        timestamp: this.lastRefresh
      });

      return {
        success: true,
        jobs: jobsWithRefreshInfo,
        sources: successfulSources,
        failedSources,
        refreshTime,
        total: jobsWithRefreshInfo.length
      };

    } catch (error) {
      console.error('‚ùå Âº∫Âà∂Âà∑Êñ∞Â§±Ë¥•:', error);
      this.refreshStats.failedRefreshes++;
      
      return {
        success: false,
        error: error.message,
        jobs: [],
        sources: []
      };
    } finally {
      this.isRefreshing = false;
    }
  }

  // Âà∑Êñ∞JobspressoÊï∞ÊçÆ
  async refreshJobspresso(searchTerm, filters, timestamp) {
    try {
      console.log('üîç Âà∑Êñ∞JobspressoÊï∞ÊçÆ...');
      
      // Ê∑ªÂä†Êó∂Èó¥Êà≥ÂèÇÊï∞Èò≤Ê≠¢ÁºìÂ≠ò
      const jobs = await fetchJobspressoJobs(searchTerm, filters.category);
      
      return {
        success: true,
        jobs: jobs.map(job => ({
          ...job,
          source: 'Jobspresso',
          fetchedAt: new Date().toISOString(),
          refreshTimestamp: timestamp
        })),
        source: 'Jobspresso'
      };
    } catch (error) {
      return {
        success: false,
        error: error.message,
        jobs: [],
        source: 'Jobspresso'
      };
    }
  }

  // Âà∑Êñ∞RemoteOKÊï∞ÊçÆ
  async refreshRemoteOK(timestamp) {
    try {
      console.log('üîç Âà∑Êñ∞RemoteOKÊï∞ÊçÆ...');
      
      const jobs = await fetchRemoteOKJobs();
      
      return {
        success: true,
        jobs: jobs.map(job => ({
          ...job,
          source: 'RemoteOK',
          fetchedAt: new Date().toISOString(),
          refreshTimestamp: timestamp
        })),
        source: 'RemoteOK'
      };
    } catch (error) {
      return {
        success: false,
        error: error.message,
        jobs: [],
        source: 'RemoteOK'
      };
    }
  }

  // Âà∑Êñ∞WeWorkRemotelyÊï∞ÊçÆ
  async refreshWeWorkRemotely(searchTerm, filters, timestamp) {
    try {
      console.log('üîç Âà∑Êñ∞WeWorkRemotelyÊï∞ÊçÆ...');
      
      const jobs = await fetchWeWorkRemotelyJobs(searchTerm, filters.category);
      
      return {
        success: true,
        jobs: jobs.map(job => ({
          ...job,
          source: 'WeWorkRemotely',
          fetchedAt: new Date().toISOString(),
          refreshTimestamp: timestamp
        })),
        source: 'WeWorkRemotely'
      };
    } catch (error) {
      return {
        success: false,
        error: error.message,
        jobs: [],
        source: 'WeWorkRemotely'
      };
    }
  }

  // Ê∏ÖÈô§ÊâÄÊúâÁºìÂ≠ò
  async clearAllCaches() {
    try {
      console.log('üóëÔ∏è Ê∏ÖÈô§ÊâÄÊúâÁºìÂ≠ò...');
      
      // Ê∏ÖÈô§ÊµèËßàÂô®ÁºìÂ≠ò
      if ('caches' in window) {
        const cacheNames = await caches.keys();
        await Promise.all(
          cacheNames.map(cacheName => caches.delete(cacheName))
        );
      }
      
      // Ê∏ÖÈô§localStorage‰∏≠ÁöÑÁºìÂ≠ò
      const keysToRemove = [];
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && (key.includes('job') || key.includes('cache'))) {
          keysToRemove.push(key);
        }
      }
      keysToRemove.forEach(key => localStorage.removeItem(key));
      
      // Ê∏ÖÈô§sessionStorage‰∏≠ÁöÑÁºìÂ≠ò
      const sessionKeysToRemove = [];
      for (let i = 0; i < sessionStorage.length; i++) {
        const key = sessionStorage.key(i);
        if (key && (key.includes('job') || key.includes('cache'))) {
          sessionKeysToRemove.push(key);
        }
      }
      sessionKeysToRemove.forEach(key => sessionStorage.removeItem(key));
      
      console.log('‚úÖ ÁºìÂ≠òÊ∏ÖÈô§ÂÆåÊàê');
    } catch (error) {
      console.warn('‚ö†Ô∏è ÁºìÂ≠òÊ∏ÖÈô§Â§±Ë¥•:', error);
    }
  }

  // ÂéªÈáçÂ§ÑÁêÜ
  deduplicateJobs(jobs) {
    const seen = new Map();
    const uniqueJobs = [];

    for (const job of jobs) {
      const key = `${job.title.toLowerCase().trim()}-${job.company.toLowerCase().trim()}`;
      
      if (!seen.has(key)) {
        seen.set(key, true);
        uniqueJobs.push(job);
      }
    }

    return uniqueJobs;
  }

  // ÊåâÊñ∞È≤úÂ∫¶ÊéíÂ∫è
  sortJobsByFreshness(jobs) {
    return jobs.sort((a, b) => {
      // ‰ºòÂÖàÊòæÁ§∫‰ªäÂ§©ÂèëÂ∏ÉÁöÑÂ∑•‰Ωú
      if (a.postedDate === 'Today' && b.postedDate !== 'Today') return -1;
      if (a.postedDate !== 'Today' && b.postedDate === 'Today') return 1;
      
      // ÊåâÊï∞ÊçÆÊ∫ê‰ºòÂÖàÁ∫ßÊéíÂ∫è (Jobspresso > RemoteOK > WeWorkRemotely)
      const sourcePriority = { 'Jobspresso': 1, 'RemoteOK': 2, 'WeWorkRemotely': 3 };
      const priorityA = sourcePriority[a.source] || 999;
      const priorityB = sourcePriority[b.source] || 999;
      
      if (priorityA !== priorityB) return priorityA - priorityB;
      
      // ÊåâËé∑ÂèñÊó∂Èó¥ÊéíÂ∫èÔºàÊúÄÊñ∞ÁöÑÂú®ÂâçÔºâ
      const timeA = new Date(a.fetchedAt || 0).getTime();
      const timeB = new Date(b.fetchedAt || 0).getTime();
      
      return timeB - timeA;
    });
  }

  // Ê∑ªÂä†Âà∑Êñ∞ÁõëÂê¨Âô®
  addRefreshListener(callback) {
    this.refreshListeners.add(callback);
    return () => this.refreshListeners.delete(callback);
  }

  // ÈÄöÁü•Âà∑Êñ∞ÁõëÂê¨Âô®
  notifyRefreshListeners(data) {
    this.refreshListeners.forEach(callback => {
      try {
        callback(data);
      } catch (error) {
        console.error('Âà∑Êñ∞ÁõëÂê¨Âô®ÈîôËØØ:', error);
      }
    });
  }

  // Ëé∑ÂèñÂà∑Êñ∞Áä∂ÊÄÅ
  getRefreshStatus() {
    return {
      isRefreshing: this.isRefreshing,
      lastRefresh: this.lastRefresh,
      stats: this.refreshStats
    };
  }

  // Ê£ÄÊü•ÊòØÂê¶ÈúÄË¶ÅÂà∑Êñ∞
  shouldRefresh() {
    if (!this.lastRefresh) return true;
    
    const timeSinceLastRefresh = Date.now() - this.lastRefresh.getTime();
    const refreshThreshold = 30 * 1000; // 30Áßí
    
    return timeSinceLastRefresh > refreshThreshold;
  }

  // È°µÈù¢Âä†ËΩΩÊó∂ÁöÑËá™Âä®Âà∑Êñ∞
  async autoRefreshOnPageLoad(searchTerm = '', filters = {}) {
    console.log('üîÑ È°µÈù¢Âä†ËΩΩÔºåËá™Âä®Âà∑Êñ∞Êï∞ÊçÆ...');
    return await this.forceRefreshAllSources(searchTerm, filters);
  }
}

// ÂàõÂª∫ÂÖ®Â±ÄÂÆû‰æã
const refreshService = new RefreshService();

export default refreshService;
