      console.log(`ÊêúÁ¥¢ "${searchTermData}" ÊâæÂà∞ ${results.length} ‰∏™Áõ∏ÂÖ≥Â∑•‰Ωú`);
      console.log('Ââç5‰∏™ÊúÄÁõ∏ÂÖ≥ÁöÑÂ∑•‰Ωú:', results.slice(0, 5).map(job => ({
