  };
  
  // Â§ÑÁêÜÁî≥ËØ∑Â∑•‰Ωú
  const handleApplyJob = (jobId) => {
    console.log(`Applying for job: ${jobId}`);
    
    // Áõ¥Êé•‰ΩøÁî®selectedJobÔºåËÄå‰∏çÊòØ‰ªéjobsÊï∞ÁªÑ‰∏≠Êü•Êâæ
    const job = selectedJob;
    
    if (!job) {
