    }
    
    // ËØ¶ÁªÜËÆ∞ÂΩïÂ∑•‰ΩúÂØπË±°ÔºåÂ∏ÆÂä©Ë∞ÉËØï
    console.log('Selected job details:', {
      id: job.id,
      title: job.title,
      company: job.company,
      source: job.source,
      sourceUrl: job.sourceUrl,
      sourceId: job.sourceId
    });
    
    // ÂàõÂª∫‰∏Ä‰∏™ÈöêËóèÁöÑaÊ†áÁ≠æÊù•ÊâìÂºÄÈìæÊé•ÔºåËøôÊ†∑ÂèØ‰ª•ÈÅøÂÖçÂºπÂá∫Á™óÂè£Ë¢´ÈòªÊ≠¢
    const openLink = (url) => {
      console.log(`Attempting to open URL: ${url}`);
      
      // ÂàõÂª∫‰∏Ä‰∏™ÈöêËóèÁöÑaÊ†áÁ≠æ
      const link = document.createElement('a');
      link.href = url;
      link.target = '_blank';
