    };
    
    let finalUrl = '';
    
    if (job.sourceUrl) {
      // Â¶ÇÊûúÊúâÊ∫êURLÔºå‰ΩøÁî®ÂÆÉ
      console.log(`Using source URL: ${job.sourceUrl}`);
      finalUrl = job.sourceUrl;
    } else if (job.source) {
      // Â¶ÇÊûúÊ≤°ÊúâÊ∫êURL‰ΩÜÊúâÊù•Ê∫ê‰ø°ÊÅØÔºåÂ∞ùËØïÊûÑÂª∫URL
      switch (job.source) {
        case 'LinkedIn':
          finalUrl = `https://www.linkedin.com/jobs/view/${job.sourceId || job.id}`;
