      alert('Êó†Ê≥ïÊâæÂà∞Á≤æÁ°ÆÁöÑÂ∑•‰ΩúÈìæÊé•ÔºåÂ∞Ü‰ΩøÁî®GoogleÊêúÁ¥¢');
    }
    
    // Â∞ùËØïÊâìÂºÄÈìæÊé•
    if (finalUrl) {
      openLink(finalUrl);
    } else {
      alert('Êó†Ê≥ïÁîüÊàêÊúâÊïàÁöÑÂ∑•‰ΩúÈìæÊé•');
    }
  };

  return (
    <div className={`job-feed ${isDrawerOpen ? 'drawer-open' : ''}`}>
