import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from '../components/Header';
import JobCard from '../components/JobCard';
import JobDetailsDrawer from '../components/JobDetailsDrawer';
// ÁßªÈô§mockÊï∞ÊçÆ‰æùËµñÔºåÊ∑ªÂä†ÂÆûÊó∂Âà∑Êñ∞ÊúçÂä°
import jobService from '../services/jobService';
import fallbackJobService from '../services/fallbackJobService';
import realTimeJobService from '../services/RealTimeJobService';
import refreshService from '../services/RefreshService';
import './JobFeed.css';

const JobFeed = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searching, setSearching] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);
  const [dataSources, setDataSources] = useState([]);
  const [dynamicCategories, setDynamicCategories] = useState([]);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [realTimeStats, setRealTimeStats] = useState(null);
  const [refreshStats, setRefreshStats] = useState(null);
  const observer = useRef();
  const lastJobElementRef = useCallback(node => {
    if (loading || loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        loadMoreJobs();
      }
    });
    if (node) observer.current.observe(node);
  }, [loading, loadingMore, hasMore]);

  // Âä†ËΩΩÊõ¥Â§öÂ∑•‰Ωú
  const loadMoreJobs = async () => {
    if (loadingMore || !hasMore) return;
    
    setLoadingMore(true);
    try {
      const nextPage = page + 1;
      const designSearchTerms = searchTerm || '';
      
      const result = await jobService.searchRemoteJobs(designSearchTerms, {
        ...activeFilters,
        category: 'design',
        page: nextPage
      });
      
      if (result.jobs.length === 0) {
        setHasMore(false);
      } else {
        // Â§ÑÁêÜÊñ∞Âä†ËΩΩÁöÑÂ∑•‰ΩúÊï∞ÊçÆ
        const newJobs = filterJobs(result.jobs);
        
        if (newJobs.length === 0) {
          setHasMore(false);
        } else {
          setJobs(prevJobs => [...prevJobs, ...newJobs]);
          setFilteredJobs(prevFiltered => [...prevFiltered, ...newJobs]);
          setPage(nextPage);
          
          // Êõ¥Êñ∞Âä®ÊÄÅÂàÜÁ±ªÔºàÂü∫‰∫éÊâÄÊúâÂ∑≤Âä†ËΩΩÁöÑÂ∑•‰ΩúÔºâ
          const allLoadedJobs = [...jobs, ...newJobs];
          const categories = generateDynamicCategories(allLoadedJobs);
          setDynamicCategories(categories);
        }
      }
    } catch (error) {
      console.error('Failed to load more jobs:', error);
    } finally {
      setLoadingMore(false);
    }
  };

  // Ê†πÊçÆÂ∑•‰ΩúÊï∞ÊçÆËá™Âä®ÁîüÊàêÂàÜÁ±ª - ÊîπËøõÁâàÊú¨ÔºåÊõ¥ÂáÜÁ°ÆÂú∞ÂåπÈÖçÂ∑•‰Ωú
  const generateDynamicCategories = (jobs) => {
    console.log('ÁîüÊàêÂä®ÊÄÅÂàÜÁ±ªÔºåÂ∑•‰ΩúÊï∞Èáè:', jobs.length);
    
    // ÂÆö‰πâÊõ¥Á≤æÁ°ÆÁöÑÂàÜÁ±ªËßÑÂàôÂíåÂÖ≥ÈîÆËØç
    const categoryRules = {
      'ux-designer': {
        label: 'UX Designer',
        primaryKeywords: ['ux designer', 'user experience designer', 'ux/ui designer', 'ui/ux designer'],
        secondaryKeywords: ['ux', 'user experience', 'interaction design', 'usability', 'user research'],
        excludeKeywords: ['ux writer', 'ux content'],
        count: 0,
        jobs: []
      },
      'ui-designer': {
        label: 'UI Designer',
        primaryKeywords: ['ui designer', 'user interface designer', 'interface designer'],
        secondaryKeywords: ['ui', 'user interface', 'visual design', 'interface design'],
        excludeKeywords: ['ui developer', 'ui engineer'],
        count: 0,
        jobs: []
      },
      'product-designer': {
        label: 'Product Designer',
        primaryKeywords: ['product designer', 'digital product designer'],
        secondaryKeywords: ['product design', 'design lead', 'senior designer'],
        excludeKeywords: ['product manager', 'product owner'],
        count: 0,
        jobs: []
      },
      'frontend-developer': {
        label: 'Frontend Developer',
        primaryKeywords: ['frontend developer', 'front-end developer', 'front end developer', 'react developer', 'vue developer', 'angular developer'],
        secondaryKeywords: ['frontend', 'front-end', 'javascript', 'react', 'vue', 'angular', 'html', 'css'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'backend-developer': {
        label: 'Backend Developer',
        primaryKeywords: ['backend developer', 'back-end developer', 'server developer', 'api developer'],
        secondaryKeywords: ['backend', 'back-end', 'server', 'api', 'node.js', 'python', 'java', 'php', 'ruby'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'fullstack-developer': {
        label: 'Full Stack Developer',
        primaryKeywords: ['full stack developer', 'fullstack developer', 'full-stack developer'],
        secondaryKeywords: ['full stack', 'fullstack', 'full-stack'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'data-scientist': {
        label: 'Data Scientist',
        primaryKeywords: ['data scientist', 'machine learning engineer', 'ai engineer'],
        secondaryKeywords: ['data science', 'machine learning', 'artificial intelligence', 'analytics', 'big data'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'devops-engineer': {
        label: 'DevOps Engineer',
        primaryKeywords: ['devops engineer', 'site reliability engineer', 'sre', 'cloud engineer'],
        secondaryKeywords: ['devops', 'infrastructure', 'cloud', 'aws', 'azure', 'kubernetes', 'docker'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'mobile-developer': {
        label: 'Mobile Developer',
        primaryKeywords: ['mobile developer', 'ios developer', 'android developer', 'react native developer'],
        secondaryKeywords: ['mobile', 'ios', 'android', 'react native', 'flutter', 'swift', 'kotlin'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'graphic-designer': {
        label: 'Graphic Designer',
        primaryKeywords: ['graphic designer', 'visual designer', 'brand designer'],
        secondaryKeywords: ['graphic design', 'visual design', 'brand design', 'creative design'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'marketing-specialist': {
        label: 'Marketing Specialist',
        primaryKeywords: ['marketing specialist', 'digital marketing', 'marketing manager', 'growth marketer'],
        secondaryKeywords: ['marketing', 'content marketing', 'social media', 'seo', 'sem', 'growth'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      },
      'project-manager': {
        label: 'Project Manager',
        primaryKeywords: ['project manager', 'program manager', 'scrum master', 'product manager'],
        secondaryKeywords: ['project management', 'agile', 'scrum', 'product management'],
        excludeKeywords: [],
        count: 0,
        jobs: []
      }
    };
    
    // ÂàÜÊûêÊØè‰∏™Â∑•‰ΩúÔºå‰ΩøÁî®Êõ¥Êô∫ËÉΩÁöÑÂåπÈÖçÈÄªËæë
    jobs.forEach(job => {
      const title = job.title.toLowerCase();
      const description = job.description ? job.description.toLowerCase() : '';
      const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
      
      // ‰∏∫ÊØè‰∏™ÂàÜÁ±ªËÆ°ÁÆóÂåπÈÖçÂàÜÊï∞
      Object.entries(categoryRules).forEach(([categoryId, categoryInfo]) => {
        let matchScore = 0;
        
        // Ê£ÄÊü•‰∏ªË¶ÅÂÖ≥ÈîÆËØçÔºàÈ´òÊùÉÈáçÔºâ
        const primaryMatch = categoryInfo.primaryKeywords.some(keyword => 
          title.includes(keyword) || description.includes(keyword)
        );
        if (primaryMatch) matchScore += 10;
        
        // Ê£ÄÊü•Ê¨°Ë¶ÅÂÖ≥ÈîÆËØçÔºà‰ΩéÊùÉÈáçÔºâ
        const secondaryMatches = categoryInfo.secondaryKeywords.filter(keyword => 
          title.includes(keyword) || description.includes(keyword) || skills.includes(keyword)
        ).length;
        matchScore += secondaryMatches * 2;
        
        // Ê£ÄÊü•ÊéíÈô§ÂÖ≥ÈîÆËØçÔºàË¥üÊùÉÈáçÔºâ
        const excludeMatch = categoryInfo.excludeKeywords.some(keyword => 
          title.includes(keyword) || description.includes(keyword)
        );
        if (excludeMatch) matchScore -= 5;
        
        // Â¶ÇÊûúÂåπÈÖçÂàÜÊï∞Ë∂≥Â§üÈ´òÔºåÂàôÂΩíÁ±ªÂà∞Ê≠§ÂàÜÁ±ª
        if (matchScore >= 5) {
          categoryInfo.count++;
          categoryInfo.jobs.push(job);
        }
      });
    });
    
    // Âè™ËøîÂõûÊúâÂ∑•‰ΩúÁöÑÂàÜÁ±ªÔºåÂπ∂ÊåâÊï∞ÈáèÊéíÂ∫è
    const validCategories = Object.entries(categoryRules)
      .filter(([_, categoryInfo]) => categoryInfo.count > 0)
      .sort(([, a], [, b]) => b.count - a.count)
      .slice(0, 8) // ÊúÄÂ§öÊòæÁ§∫8‰∏™ÂàÜÁ±ª
      .map(([categoryId, categoryInfo]) => ({
        id: categoryId,
        label: `${categoryInfo.label} (${categoryInfo.count})`,
        count: categoryInfo.count
      }));
    
    console.log('ÁîüÊàêÁöÑÂä®ÊÄÅÂàÜÁ±ª:', validCategories);
    
    // ËæìÂá∫ËØ¶ÁªÜÁöÑÂàÜÁ±ª‰ø°ÊÅØÁî®‰∫éË∞ÉËØï
    validCategories.forEach(category => {
      const categoryRule = categoryRules[category.id];
      console.log(`${category.label}:`, categoryRule.jobs.slice(0, 3).map(job => job.title));
    });
    
    return validCategories;
  };

  // ËøáÊª§ÂáΩÊï∞Ôºö‰øùÁïôÊâÄÊúâÂ∑•‰ΩúÔºå‰∏çÂÜçÈôêÂà∂‰∏∫ËÆæËÆ°Â∑•‰Ωú
  const filterJobs = (jobs) => {
    console.log('Â§ÑÁêÜÂ∑•‰ΩúÊï∞ÊçÆÔºåÊÄªÊï∞:', jobs.length);
    return jobs; // ËøîÂõûÊâÄÊúâÂ∑•‰Ωú
  };

  // ÂÆûÊó∂Êï∞ÊçÆÊõ¥Êñ∞ÁõëÂê¨Âô® - ÈõÜÊàêRefreshServiceÁ°Æ‰øùÊØèÊ¨°ÈÉΩËé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
  useEffect(() => {
    console.log('üöÄ ÂàùÂßãÂåñÂÆûÊó∂Â∑•‰ΩúÊï∞ÊçÆÊúçÂä°ÂíåÂà∑Êñ∞ÊúçÂä°');
    
    // ÂêØÂä®ÂÆûÊó∂Êï∞ÊçÆÊúçÂä°
    realTimeJobService.startRealTimeUpdates();
    
    // Ê∑ªÂä†Êï∞ÊçÆÊõ¥Êñ∞ÁõëÂê¨Âô®
    const unsubscribeRealTime = realTimeJobService.addUpdateListener((data) => {
      console.log('üì° Êî∂Âà∞ÂÆûÊó∂Êï∞ÊçÆÊõ¥Êñ∞:', data);
      
      // Êõ¥Êñ∞Â∑•‰ΩúÊï∞ÊçÆ
      setJobs(data.jobs);
      setDataSources(data.sources);
      setLastUpdate(data.lastUpdate);
      setRealTimeStats(data.stats);
      
      // ÁîüÊàêÂä®ÊÄÅÂàÜÁ±ª
      const categories = generateDynamicCategories(data.jobs);
      setDynamicCategories(categories);
      
      // ÈáçÊñ∞Â∫îÁî®ÂΩìÂâçÁöÑÊêúÁ¥¢ÂíåÁ≠õÈÄâ
      applyFiltersToJobs(activeFilters, data.jobs, searchTerm);
    });

    // Ê∑ªÂä†Âà∑Êñ∞ÊúçÂä°ÁõëÂê¨Âô®
    const unsubscribeRefresh = refreshService.addRefreshListener((data) => {
      console.log('üîÑ Êî∂Âà∞Âà∑Êñ∞ÊúçÂä°Êõ¥Êñ∞:', data);
      
      // Êõ¥Êñ∞Â∑•‰ΩúÊï∞ÊçÆ
      setJobs(data.jobs);
      setDataSources(data.sources);
      setLastUpdate(data.timestamp);
      
      // ÁîüÊàêÂä®ÊÄÅÂàÜÁ±ª
      const categories = generateDynamicCategories(data.jobs);
      setDynamicCategories(categories);
      
      // ÈáçÊñ∞Â∫îÁî®ÂΩìÂâçÁöÑÊêúÁ¥¢ÂíåÁ≠õÈÄâ
      applyFiltersToJobs(activeFilters, data.jobs, searchTerm);
    });
    
    // Ëé∑ÂèñÂàùÂßãÊï∞ÊçÆ - ‰ºòÂÖà‰ΩøÁî®Âà∑Êñ∞ÊúçÂä°Âº∫Âà∂Ëé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
    const initializeData = async () => {
      setLoading(true);
      try {
        console.log('üîÑ È°µÈù¢Âä†ËΩΩÔºåÂº∫Âà∂Âà∑Êñ∞Ëé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ...');
        
        // ‰ΩøÁî®Âà∑Êñ∞ÊúçÂä°Âº∫Âà∂Ëé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
        const refreshResult = await refreshService.autoRefreshOnPageLoad(searchTerm, { 
          category: activeFilters.length > 0 ? activeFilters[0] : '' 
        });
        
        if (refreshResult.success && refreshResult.jobs.length > 0) {
          console.log('‚úÖ Âà∑Êñ∞ÊúçÂä°Ëé∑ÂèñÊï∞ÊçÆÊàêÂäü:', refreshResult.jobs.length, '‰∏™Â∑•‰Ωú');
          setJobs(refreshResult.jobs);
          setDataSources(refreshResult.sources);
          setLastUpdate(new Date());
          
          const categories = generateDynamicCategories(refreshResult.jobs);
          setDynamicCategories(categories);
          
          applyFiltersToJobs(activeFilters, refreshResult.jobs, searchTerm);
        } else {
          // Â¶ÇÊûúÂà∑Êñ∞ÊúçÂä°Â§±Ë¥•ÔºåÂ∞ùËØï‰ΩøÁî®ÂÆûÊó∂ÊúçÂä°
          console.log('‚ö†Ô∏è Âà∑Êñ∞ÊúçÂä°Â§±Ë¥•ÔºåÂ∞ùËØïÂÆûÊó∂ÊúçÂä°...');
          const currentData = realTimeJobService.getCurrentJobs();
          if (currentData.jobs.length > 0) {
            console.log('‚úÖ ‰ΩøÁî®ÂÆûÊó∂ÊúçÂä°Áé∞ÊúâÊï∞ÊçÆ:', currentData.jobs.length, '‰∏™Â∑•‰Ωú');
            setJobs(currentData.jobs);
            setDataSources(currentData.sources);
            setLastUpdate(currentData.lastUpdate);
            setRealTimeStats(currentData.stats);
            
            const categories = generateDynamicCategories(currentData.jobs);
            setDynamicCategories(categories);
            
            applyFiltersToJobs(activeFilters, currentData.jobs, searchTerm);
          } else {
            console.log('üîÑ Ëß¶ÂèëÂÆûÊó∂ÊúçÂä°È¶ñÊ¨°Êï∞ÊçÆÊõ¥Êñ∞');
            await realTimeJobService.forceUpdate();
          }
        }
      } catch (error) {
        console.error('‚ùå ÂàùÂßãÂåñÊï∞ÊçÆÂ§±Ë¥•:', error);
        // ‰∏çÂÜç‰ΩøÁî®mockÊï∞ÊçÆÔºåÊòæÁ§∫Á©∫Áä∂ÊÄÅ
        setJobs([]);
        setFilteredJobs([]);
        setDataSources(['No Data Available']);
        setDynamicCategories([]);
      } finally {
        setLoading(false);
      }
    };
    
    initializeData();
    
    // Ê∏ÖÁêÜÂáΩÊï∞
    return () => {
      console.log('üõë Ê∏ÖÁêÜÂÆûÊó∂Êï∞ÊçÆÊúçÂä°ÂíåÂà∑Êñ∞ÊúçÂä°');
      unsubscribeRealTime();
      unsubscribeRefresh();
      realTimeJobService.stopRealTimeUpdates();
    };
  }, []); // Âè™Âú®ÁªÑ‰ª∂ÊåÇËΩΩÊó∂ËøêË°å‰∏ÄÊ¨°

  const handleSearch = (term) => {
    // Êõ¥Êñ∞ÊêúÁ¥¢ËØçÁä∂ÊÄÅ
    if (term !== undefined && term !== null) {
      setSearchTerm(term);
    }
    
    // ËÆæÁΩÆÊêúÁ¥¢Áä∂ÊÄÅ‰∏∫Ê≠£Âú®ÊêúÁ¥¢
    setSearching(true);
    
    // ‰ΩøÁî®setTimeoutÊ®°ÊãüÊêúÁ¥¢Âª∂ËøüÔºåÊèê‰æõÊõ¥Â•ΩÁöÑÁî®Êà∑‰ΩìÈ™å
    setTimeout(() => {
      // ÈáçÊñ∞Â∫îÁî®Á≠õÈÄâÂô®ÔºåÂåÖÊã¨Êñ∞ÁöÑÊêúÁ¥¢ËØç
      applyFiltersToJobs(activeFilters);
      setSearching(false);
    }, 300); // 300msÁöÑÂª∂ËøüÔºåÊèê‰æõÊõ¥ÊµÅÁïÖÁöÑÊêúÁ¥¢‰ΩìÈ™å
  };

  const handleFilter = (filterIds) => {
    console.log('Á≠õÈÄâÂô®Êõ¥Êñ∞:', filterIds);
    
    // Êõ¥Êñ∞Ê¥ªÂä®Á≠õÈÄâÂô®Áä∂ÊÄÅ
    setActiveFilters(filterIds);
    
    // Á´ãÂç≥Â∫îÁî®Á≠õÈÄâÂà∞ÂΩìÂâçÂ∑≤Âä†ËΩΩÁöÑÂ∑•‰Ωú
    applyFiltersToJobs(filterIds);
  };

  // ÊâãÂä®Âà∑Êñ∞Êï∞ÊçÆ - ‰ΩøÁî®RefreshServiceÁ°Æ‰øùËé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
  const handleRefresh = async () => {
    if (isRefreshing) return;
    
    console.log('üîÑ ÊâãÂä®Âà∑Êñ∞Â∑•‰ΩúÊï∞ÊçÆ');
    setIsRefreshing(true);
    
    try {
      // ‰ºòÂÖà‰ΩøÁî®RefreshServiceÂº∫Âà∂Âà∑Êñ∞ÊâÄÊúâÊï∞ÊçÆÊ∫ê
      const refreshResult = await refreshService.forceRefreshAllSources(searchTerm, {
        category: activeFilters.length > 0 ? activeFilters[0] : ''
      });
      
      if (refreshResult.success) {
        console.log('‚úÖ RefreshServiceÂà∑Êñ∞ÊàêÂäü:', refreshResult.jobs.length, '‰∏™Â∑•‰Ωú');
        setRefreshStats(refreshResult);
        // Êï∞ÊçÆ‰ºöÈÄöËøáÁõëÂê¨Âô®Ëá™Âä®Êõ¥Êñ∞UI
      } else {
        console.warn('‚ö†Ô∏è RefreshServiceÂà∑Êñ∞Â§±Ë¥•ÔºåÂ∞ùËØïRealTimeJobService...');
        
        // Â¶ÇÊûúRefreshServiceÂ§±Ë¥•ÔºåÂõûÈÄÄÂà∞RealTimeJobService
        const result = await realTimeJobService.forceUpdate();
        if (result.success) {
          console.log('‚úÖ RealTimeJobServiceÂà∑Êñ∞ÊàêÂäü');
        } else {
          console.error('‚ùå ÊâÄÊúâÂà∑Êñ∞ÊñπÊ≥ïÈÉΩÂ§±Ë¥•:', result.error);
        }
      }
    } catch (error) {
      console.error('‚ùå ÊâãÂä®Âà∑Êñ∞ÂºÇÂ∏∏:', error);
    } finally {
      setIsRefreshing(false);
    }
  };

  // Ê†ºÂºèÂåñÊúÄÂêéÊõ¥Êñ∞Êó∂Èó¥
  const formatLastUpdate = (date) => {
    if (!date) return 'Êú™Áü•';
    
    const now = new Date();
    const diff = now - new Date(date);
    const minutes = Math.floor(diff / (1000 * 60));
    
    if (minutes < 1) return 'ÂàöÂàö';
    if (minutes < 60) return `${minutes}ÂàÜÈíüÂâç`;
    
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}Â∞èÊó∂Ââç`;
    
    const days = Math.floor(hours / 24);
    return `${days}Â§©Ââç`;
  };

  // Áªü‰∏ÄÁöÑÂ∑•‰ΩúÂàÜÁ±ªÂåπÈÖçÂáΩÊï∞ - ‰∏égenerateDynamicCategories‰ΩøÁî®Áõ∏ÂêåÁöÑÈÄªËæë
  const getJobCategoryMatch = (job, categoryId) => {
    const title = job.title.toLowerCase();
    const description = job.description ? job.description.toLowerCase() : '';
    const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
    
    // ‰ΩøÁî®‰∏égenerateDynamicCategoriesÁõ∏ÂêåÁöÑÂàÜÁ±ªËßÑÂàô
    const categoryRules = {
      'ux-designer': {
        primaryKeywords: ['ux designer', 'user experience designer', 'ux/ui designer', 'ui/ux designer'],
        secondaryKeywords: ['ux', 'user experience', 'interaction design', 'usability', 'user research'],
        excludeKeywords: ['ux writer', 'ux content']
      },
      'ui-designer': {
        primaryKeywords: ['ui designer', 'user interface designer', 'interface designer'],
        secondaryKeywords: ['ui', 'user interface', 'visual design', 'interface design'],
        excludeKeywords: ['ui developer', 'ui engineer']
      },
      'product-designer': {
        primaryKeywords: ['product designer', 'digital product designer'],
        secondaryKeywords: ['product design', 'design lead', 'senior designer'],
        excludeKeywords: ['product manager', 'product owner']
      },
      'frontend-developer': {
        primaryKeywords: ['frontend developer', 'front-end developer', 'front end developer', 'react developer', 'vue developer', 'angular developer'],
        secondaryKeywords: ['frontend', 'front-end', 'javascript', 'react', 'vue', 'angular', 'html', 'css'],
        excludeKeywords: []
      },
      'backend-developer': {
        primaryKeywords: ['backend developer', 'back-end developer', 'server developer', 'api developer'],
        secondaryKeywords: ['backend', 'back-end', 'server', 'api', 'node.js', 'python', 'java', 'php', 'ruby'],
        excludeKeywords: []
      },
      'fullstack-developer': {
        primaryKeywords: ['full stack developer', 'fullstack developer', 'full-stack developer'],
        secondaryKeywords: ['full stack', 'fullstack', 'full-stack'],
        excludeKeywords: []
      },
      'data-scientist': {
        primaryKeywords: ['data scientist', 'machine learning engineer', 'ai engineer'],
        secondaryKeywords: ['data science', 'machine learning', 'artificial intelligence', 'analytics', 'big data'],
        excludeKeywords: []
      },
      'devops-engineer': {
        primaryKeywords: ['devops engineer', 'site reliability engineer', 'sre', 'cloud engineer'],
        secondaryKeywords: ['devops', 'infrastructure', 'cloud', 'aws', 'azure', 'kubernetes', 'docker'],
        excludeKeywords: []
      },
      'mobile-developer': {
        primaryKeywords: ['mobile developer', 'ios developer', 'android developer', 'react native developer'],
        secondaryKeywords: ['mobile', 'ios', 'android', 'react native', 'flutter', 'swift', 'kotlin'],
        excludeKeywords: []
      },
      'graphic-designer': {
        primaryKeywords: ['graphic designer', 'visual designer', 'brand designer'],
        secondaryKeywords: ['graphic design', 'visual design', 'brand design', 'creative design'],
        excludeKeywords: []
      },
      'marketing-specialist': {
        primaryKeywords: ['marketing specialist', 'digital marketing', 'marketing manager', 'growth marketer'],
        secondaryKeywords: ['marketing', 'content marketing', 'social media', 'seo', 'sem', 'growth'],
        excludeKeywords: []
      },
      'project-manager': {
        primaryKeywords: ['project manager', 'program manager', 'scrum master', 'product manager'],
        secondaryKeywords: ['project management', 'agile', 'scrum', 'product management'],
        excludeKeywords: []
      }
    };
    
    const categoryInfo = categoryRules[categoryId];
    if (!categoryInfo) return false;
    
    let matchScore = 0;
    
    // Ê£ÄÊü•‰∏ªË¶ÅÂÖ≥ÈîÆËØçÔºàÈ´òÊùÉÈáçÔºâ
    const primaryMatch = categoryInfo.primaryKeywords.some(keyword => 
      title.includes(keyword) || description.includes(keyword)
    );
    if (primaryMatch) matchScore += 10;
    
    // Ê£ÄÊü•Ê¨°Ë¶ÅÂÖ≥ÈîÆËØçÔºà‰ΩéÊùÉÈáçÔºâ
    const secondaryMatches = categoryInfo.secondaryKeywords.filter(keyword => 
      title.includes(keyword) || description.includes(keyword) || skills.includes(keyword)
    ).length;
    matchScore += secondaryMatches * 2;
    
    // Ê£ÄÊü•ÊéíÈô§ÂÖ≥ÈîÆËØçÔºàË¥üÊùÉÈáçÔºâ
    const excludeMatch = categoryInfo.excludeKeywords.some(keyword => 
      title.includes(keyword) || description.includes(keyword)
    );
    if (excludeMatch) matchScore -= 5;
    
    // ËøîÂõûÊòØÂê¶ÂåπÈÖçÔºà‰ΩøÁî®‰∏égenerateDynamicCategoriesÁõ∏ÂêåÁöÑÈòàÂÄºÔºâ
    return matchScore >= 5;
  };

  // Â¢ûÂº∫ÁöÑÊêúÁ¥¢Áõ∏ÂÖ≥ÊÄßËØÑÂàÜÂáΩÊï∞
  const calculateSearchRelevance = (job, searchTerm) => {
    if (!searchTerm) return 0;
    
    const term = searchTerm.toLowerCase();
    const title = job.title.toLowerCase();
    const company = job.company.toLowerCase();
    const location = job.location.toLowerCase();
    const description = job.description ? job.description.toLowerCase() : '';
    const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
    
    let score = 0;
    
    // Ê†áÈ¢òÂåπÈÖçÔºàÊúÄÈ´òÊùÉÈáçÔºâ
    if (title.includes(term)) {
      if (title.startsWith(term)) score += 100; // Ê†áÈ¢òÂºÄÂ§¥ÂåπÈÖç
      else if (title.split(' ').some(word => word.startsWith(term))) score += 80; // ÂçïËØçÂºÄÂ§¥ÂåπÈÖç
      else score += 60; // Ê†áÈ¢òÂåÖÂê´
    }
    
    // ÂÖ¨Âè∏ÂêçÁß∞ÂåπÈÖçÔºàÈ´òÊùÉÈáçÔºâ
    if (company.includes(term)) {
      if (company.startsWith(term)) score += 50;
      else score += 30;
    }
    
    // ÊäÄËÉΩÂåπÈÖçÔºà‰∏≠Á≠âÊùÉÈáçÔºâ
    if (skills.includes(term)) {
      const skillWords = skills.split(' ');
      if (skillWords.some(skill => skill === term)) score += 40; // Á≤æÁ°ÆÊäÄËÉΩÂåπÈÖç
      else score += 20; // ÊäÄËÉΩÂåÖÂê´
    }
    
    // ÊèèËø∞ÂåπÈÖçÔºàËæÉ‰ΩéÊùÉÈáçÔºâ
    if (description.includes(term)) {
      const termCount = (description.match(new RegExp(term, 'g')) || []).length;
      score += Math.min(termCount * 5, 25); // ÊúÄÂ§ö25ÂàÜÔºåÈÅøÂÖçÊèèËø∞ËøáÂ∫¶ÂΩ±Âìç
    }
    
    // ‰ΩçÁΩÆÂåπÈÖçÔºàËæÉ‰ΩéÊùÉÈáçÔºâ
    if (location.includes(term)) {
      score += 15;
    }
    
    // Â§öËØçÊêúÁ¥¢ÁöÑÈ¢ùÂ§ñËØÑÂàÜ
    const searchWords = term.split(' ').filter(word => word.length > 2);
    if (searchWords.length > 1) {
      const matchedWords = searchWords.filter(word => 
        title.includes(word) || company.includes(word) || skills.includes(word)
      );
      score += matchedWords.length * 10; // ÊØè‰∏™ÂåπÈÖçÁöÑËØçÈ¢ùÂ§ñ10ÂàÜ
    }
    
    return score;
  };

  // Â∫îÁî®Á≠õÈÄâÂô®Âà∞Â∑•‰ΩúÂàóË°® - ‰ΩøÁî®Áªü‰∏ÄÁöÑÂåπÈÖçÈÄªËæëÂíåÁõ∏ÂÖ≥ÊÄßÊéíÂ∫è
  const applyFiltersToJobs = (filterIds, jobsData = jobs, searchTermData = searchTerm) => {
    let results = [...jobsData];
    
    // È¶ñÂÖàÊåâÊêúÁ¥¢ËØçËøáÊª§Âπ∂ËÆ°ÁÆóÁõ∏ÂÖ≥ÊÄß
    if (searchTermData) {
      results = results
        .map(job => ({
          ...job,
          relevanceScore: calculateSearchRelevance(job, searchTermData)
        }))
        .filter(job => job.relevanceScore > 0) // Âè™‰øùÁïôÊúâÁõ∏ÂÖ≥ÊÄßÁöÑÂ∑•‰Ωú
        .sort((a, b) => b.relevanceScore - a.relevanceScore); // ÊåâÁõ∏ÂÖ≥ÊÄßÊéíÂ∫è
      
      console.log(`ÊêúÁ¥¢ "${searchTermData}" ÊâæÂà∞ ${results.length} ‰∏™Áõ∏ÂÖ≥Â∑•‰Ωú`);
      console.log('Ââç5‰∏™ÊúÄÁõ∏ÂÖ≥ÁöÑÂ∑•‰Ωú:', results.slice(0, 5).map(job => ({
        title: job.title,
        company: job.company,
        score: job.relevanceScore
      })));
    }
    
    // ÁÑ∂ÂêéÊåâËÅå‰ΩçÁ±ªÂûãÁ≠õÈÄâ - ‰ΩøÁî®‰∏éÂàÜÁ±ªÁîüÊàêÁõ∏ÂêåÁöÑÊô∫ËÉΩÂåπÈÖçÈÄªËæë
    if (filterIds && filterIds.length > 0) {
      results = results.filter(job => {
        // Ê£ÄÊü•Â∑•‰ΩúÊòØÂê¶ÂåπÈÖç‰ªª‰∏ÄÊøÄÊ¥ªÁöÑÁ≠õÈÄâÂô®
        return filterIds.some(filterId => getJobCategoryMatch(job, filterId));
      });
    }
    
    console.log(`Á≠õÈÄâÁªìÊûú: ${results.length} ‰∏™Â∑•‰Ωú (ÂéüÂßã: ${jobs.length}, Á≠õÈÄâÂô®: ${filterIds})`);
    
    // ËæìÂá∫Ë∞ÉËØï‰ø°ÊÅØÔºåÊòæÁ§∫ÊØè‰∏™Á≠õÈÄâÂô®ÂåπÈÖçÂà∞ÁöÑÂ∑•‰Ωú
    if (filterIds && filterIds.length > 0) {
      filterIds.forEach(filterId => {
        const matchedJobs = results.filter(job => getJobCategoryMatch(job, filterId));
        console.log(`Á≠õÈÄâÂô® ${filterId} ÂåπÈÖçÂà∞ ${matchedJobs.length} ‰∏™Â∑•‰Ωú:`, 
          matchedJobs.slice(0, 3).map(job => job.title));
      });
    }
    
    setFilteredJobs(results);
  };
  


  // Â§ÑÁêÜÂ∑•‰ΩúÂç°ÁâáÁÇπÂáª
  const handleJobSelect = (job) => {
    setSelectedJob(job);
    setIsDrawerOpen(true);
  };
  
  // Â§ÑÁêÜÂÖ≥Èó≠ÊäΩÂ±â
  const handleCloseDrawer = () => {
    setIsDrawerOpen(false);
  };
  
  // Â§ÑÁêÜÁî≥ËØ∑Â∑•‰Ωú
  const handleApplyJob = (jobId) => {
    console.log(`Applying for job: ${jobId}`);
    
    // Áõ¥Êé•‰ΩøÁî®selectedJobÔºåËÄå‰∏çÊòØ‰ªéjobsÊï∞ÁªÑ‰∏≠Êü•Êâæ
    const job = selectedJob;
    
    if (!job) {
      console.error('No selected job found');
      alert('Êó†Ê≥ïÊâæÂà∞ÈÄâ‰∏≠ÁöÑÂ∑•‰Ωú');
      return;
    }
    
    // ËØ¶ÁªÜËÆ∞ÂΩïÂ∑•‰ΩúÂØπË±°ÔºåÂ∏ÆÂä©Ë∞ÉËØï
    console.log('Selected job details:', {
      id: job.id,
      title: job.title,
      company: job.company,
      source: job.source,
      sourceUrl: job.sourceUrl,
      sourceId: job.sourceId
    });
    
    // ÂàõÂª∫‰∏Ä‰∏™ÈöêËóèÁöÑaÊ†áÁ≠æÊù•ÊâìÂºÄÈìæÊé•ÔºåËøôÊ†∑ÂèØ‰ª•ÈÅøÂÖçÂºπÂá∫Á™óÂè£Ë¢´ÈòªÊ≠¢
    const openLink = (url) => {
      console.log(`Attempting to open URL: ${url}`);
      
      // ÂàõÂª∫‰∏Ä‰∏™ÈöêËóèÁöÑaÊ†áÁ≠æ
      const link = document.createElement('a');
      link.href = url;
      link.target = '_blank';
      link.rel = 'noopener noreferrer';
      
      // Ê∑ªÂä†Âà∞ÊñáÊ°£‰∏≠Âπ∂Ëß¶ÂèëÁÇπÂáª
      document.body.appendChild(link);
      link.click();
      
      // Ê∏ÖÁêÜ
      setTimeout(() => {
        document.body.removeChild(link);
      }, 100);
    };
    
    let finalUrl = '';
    
    if (job.sourceUrl) {
      // Â¶ÇÊûúÊúâÊ∫êURLÔºå‰ΩøÁî®ÂÆÉ
      console.log(`Using source URL: ${job.sourceUrl}`);
      finalUrl = job.sourceUrl;
    } else if (job.source) {
      // Â¶ÇÊûúÊ≤°ÊúâÊ∫êURL‰ΩÜÊúâÊù•Ê∫ê‰ø°ÊÅØÔºåÂ∞ùËØïÊûÑÂª∫URL
      switch (job.source) {
        case 'LinkedIn':
          finalUrl = `https://www.linkedin.com/jobs/view/${job.sourceId || job.id}`;
          break;
        case 'WeWorkRemotely':
          // Â∞ùËØï‰ΩøÁî®Êõ¥ÂèØËÉΩÊ≠£Á°ÆÁöÑWeWorkRemotely URLÊ†ºÂºè
          if (job.company && job.title) {
            // Â∞ÜÂÖ¨Âè∏ÂêçÂíåËÅå‰ΩçÂêçËΩ¨Êç¢‰∏∫URLÂèãÂ•ΩÁöÑÊ†ºÂºè
            const companySlug = job.company.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            const titleSlug = job.title.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            finalUrl = `https://weworkremotely.com/remote-jobs/${companySlug}-${titleSlug}`;
          } else {
            finalUrl = `https://weworkremotely.com/remote-jobs/${job.sourceId || job.id}`;
          }
          break;
        case 'RemoteOK':
          finalUrl = `https://remoteok.io/l/${job.sourceId || job.id}`;
          break;
        default:
          // Â¶ÇÊûúÊó†Ê≥ïÁ°ÆÂÆöÊù•Ê∫êÔºåÂ∞ùËØï‰ΩøÁî®ÈÄöÁî®ÊêúÁ¥¢
          finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      }
      console.log(`Constructed URL: ${finalUrl}`);
    } else {
      // Â¶ÇÊûúÊ≤°Êúâ‰ªª‰ΩïÈìæÊé•‰ø°ÊÅØÔºåÊòæÁ§∫ÊèêÁ§∫Âπ∂‰ΩøÁî®GoogleÊêúÁ¥¢
      console.error('No source URL or source information found for job:', job);
      finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      alert('Êó†Ê≥ïÊâæÂà∞Á≤æÁ°ÆÁöÑÂ∑•‰ΩúÈìæÊé•ÔºåÂ∞Ü‰ΩøÁî®GoogleÊêúÁ¥¢');
    }
    
    // Â∞ùËØïÊâìÂºÄÈìæÊé•
    if (finalUrl) {
      openLink(finalUrl);
    } else {
      alert('Êó†Ê≥ïÁîüÊàêÊúâÊïàÁöÑÂ∑•‰ΩúÈìæÊé•');
    }
  };

  return (
    <div className={`job-feed ${isDrawerOpen ? 'drawer-open' : ''}`}>
      <Header 
        onSearch={(term) => handleSearch(term)} 
        onFilter={handleFilter}
        onRefresh={handleRefresh}
        dynamicCategories={dynamicCategories}
        isRefreshing={isRefreshing}
      />
      
      <div className="job-list-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading jobs...</p>
          </div>
        ) : searching ? (
          <div className="searching-container">
            <div className="loading-spinner small"></div>
            <p>Searching...</p>
          </div>
        ) : (
          <>
            {filteredJobs.length > 0 ? (
              <>
                <div className="results-header">
                  <div className="results-main-info">
                    <span className="results-count">
                      ÊâæÂà∞ <span className="count-highlight">{filteredJobs.length}</span> ‰∏™Â∑•‰ΩúÊú∫‰ºö
                    </span>
                    <span className="last-update-inline">
                      ÊúÄÂêéÊõ¥Êñ∞: {formatLastUpdate(lastUpdate)}
                    </span>
                    {realTimeStats && realTimeStats.newJobs > 0 && (
                      <span className="new-jobs-inline">
                        üÜï {realTimeStats.newJobs} ‰∏™Êñ∞Â∑•‰Ωú
                      </span>
                    )}
                    {realTimeStats && (
                      <span className="total-updates-inline">
                        Â∑≤Êõ¥Êñ∞ {realTimeStats.successfulUpdates} Ê¨°
                      </span>
                    )}
                    <button 
                      className={`refresh-btn-inline ${isRefreshing ? 'refreshing' : ''}`}
                      onClick={handleRefresh}
                      disabled={isRefreshing}
                      title="Âà∑Êñ∞Â∑•‰ΩúÊï∞ÊçÆ"
                    >
                      {isRefreshing ? 'üîÑ' : '‚Üª'}
                    </button>
                  </div>
                  <div className="data-sources">
                    Êï∞ÊçÆÊù•Ê∫ê: {dataSources.join(', ')}
                  </div>
                </div>
                <div className="job-list">
                  {filteredJobs.map((job, index) => {
                    if (filteredJobs.length === index + 1) {
                      return (
                        <div ref={lastJobElementRef} key={job.id}>
                          <JobCard 
                            job={job} 
                            onSelect={() => handleJobSelect(job)}
                          />
                        </div>
                      );
                    } else {
                      return (
                        <JobCard 
                          key={job.id} 
                          job={job} 
                          onSelect={() => handleJobSelect(job)}
                        />
                      );
                    }
                  })}
                  {loadingMore && (
                    <div className="loading-more">
                      <div className="loading-spinner small"></div>
                      <p>Âä†ËΩΩÊõ¥Â§öÂ∑•‰Ωú...</p>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="no-jobs-found">
                <h3>No jobs found</h3>
                <p>Try adjusting your search filters</p>
              </div>
            )}
          </>
        )}
      </div>
      
      <JobDetailsDrawer 
        job={selectedJob}
        isOpen={isDrawerOpen}
        onClose={handleCloseDrawer}
        onApply={handleApplyJob}
      />
    </div>
  );
};

export default JobFeed;
