// ÂÆûÊó∂Â∑•‰ΩúÊï∞ÊçÆÂà∑Êñ∞ÊúçÂä°
import { fetchRealRemoteJobs, fetchRemoteOKJobs, fetchWeWorkRemotelyJobs } from './realJobAPI';
import { mockJobs } from './mockData';

class RealTimeJobService {
  constructor() {
    this.jobs = [];
    this.lastUpdate = null;
    this.updateInterval = null;
    this.listeners = new Set();
    this.isUpdating = false;
    this.updateFrequency = 5 * 60 * 1000; // 5ÂàÜÈíüÊõ¥Êñ∞‰∏ÄÊ¨°
    this.sources = [];
    this.stats = {
      totalUpdates: 0,
      successfulUpdates: 0,
      failedUpdates: 0,
      lastError: null
    };
  }

  // ÂºÄÂßãÂÆûÊó∂Êõ¥Êñ∞
  startRealTimeUpdates() {
    console.log('üöÄ ÂêØÂä®ÂÆûÊó∂Â∑•‰ΩúÊï∞ÊçÆÊõ¥Êñ∞ÊúçÂä°');
    
    // Á´ãÂç≥ÊâßË°å‰∏ÄÊ¨°Êõ¥Êñ∞
    this.updateJobs();
    
    // ËÆæÁΩÆÂÆöÊúüÊõ¥Êñ∞
    this.updateInterval = setInterval(() => {
      this.updateJobs();
    }, this.updateFrequency);
    
    return this;
  }

  // ÂÅúÊ≠¢ÂÆûÊó∂Êõ¥Êñ∞
  stopRealTimeUpdates() {
    console.log('‚èπÔ∏è ÂÅúÊ≠¢ÂÆûÊó∂Â∑•‰ΩúÊï∞ÊçÆÊõ¥Êñ∞ÊúçÂä°');
    
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
    }
    
    return this;
  }

  // ÊâãÂä®Ëß¶ÂèëÊõ¥Êñ∞
  async forceUpdate() {
    console.log('üîÑ ÊâãÂä®Ëß¶ÂèëÂ∑•‰ΩúÊï∞ÊçÆÊõ¥Êñ∞');
    return await this.updateJobs(true);
  }

  // Êõ¥Êñ∞Â∑•‰ΩúÊï∞ÊçÆ
  async updateJobs(force = false) {
    if (this.isUpdating && !force) {
      console.log('‚è≥ Êõ¥Êñ∞Ê≠£Âú®ËøõË°å‰∏≠ÔºåË∑≥ËøáÊ≠§Ê¨°Êõ¥Êñ∞');
      return;
    }

    this.isUpdating = true;
    this.stats.totalUpdates++;
    
    try {
      console.log('üîÑ ÂºÄÂßãËé∑ÂèñÊúÄÊñ∞Â∑•‰ΩúÊï∞ÊçÆ...');
      
      // Âπ∂Ë°åË∞ÉÁî®Â§ö‰∏™Êï∞ÊçÆÊ∫ê
      const dataPromises = [
        this.fetchFromRemoteOK(),
        this.fetchFromWeWorkRemotely(),
        this.fetchMockData()
      ];

      const results = await Promise.allSettled(dataPromises);
      
      // ÂêàÂπ∂ÊâÄÊúâÊàêÂäüÁöÑÁªìÊûú
      let newJobs = [];
      let activeSources = [];
      
      results.forEach((result, index) => {
        if (result.status === 'fulfilled' && result.value.jobs.length > 0) {
          newJobs = [...newJobs, ...result.value.jobs];
          activeSources.push(result.value.source);
          console.log(`‚úÖ ${result.value.source}: ${result.value.jobs.length} ‰∏™Â∑•‰Ωú`);
        } else {
          const sources = ['RemoteOK', 'WeWorkRemotely', 'Mock Data'];
          console.warn(`‚ùå ${sources[index]} Ëé∑ÂèñÂ§±Ë¥•:`, result.reason?.message);
        }
      });

      // ÂéªÈáçÂ§ÑÁêÜ
      const uniqueJobs = this.deduplicateJobs(newJobs);
      
      // Ê∑ªÂä†Êó∂Èó¥Êà≥ÂíåÊñ∞Â∑•‰ΩúÊ†áËÆ∞
      const jobsWithTimestamp = uniqueJobs.map(job => ({
        ...job,
        fetchedAt: new Date().toISOString(),
        isNew: this.isNewJob(job)
      }));

      // Êõ¥Êñ∞ÂÜÖÈÉ®Áä∂ÊÄÅ
      this.jobs = jobsWithTimestamp;
      this.sources = activeSources;
      this.lastUpdate = new Date();
      this.stats.successfulUpdates++;
      this.stats.lastError = null;

      console.log(`‚úÖ Êï∞ÊçÆÊõ¥Êñ∞ÂÆåÊàê: ${uniqueJobs.length} ‰∏™ÂîØ‰∏ÄÂ∑•‰Ωú`);
      console.log(`üìä Êï∞ÊçÆÊ∫ê: ${activeSources.join(', ')}`);

      // ÈÄöÁü•ÊâÄÊúâÁõëÂê¨Âô®
      this.notifyListeners({
        jobs: this.jobs,
        sources: this.sources,
        lastUpdate: this.lastUpdate,
        stats: this.getStats()
      });

      return {
        success: true,
        jobs: this.jobs,
        sources: this.sources,
        count: this.jobs.length
      };

    } catch (error) {
      console.error('‚ùå Â∑•‰ΩúÊï∞ÊçÆÊõ¥Êñ∞Â§±Ë¥•:', error);
      this.stats.failedUpdates++;
      this.stats.lastError = error.message;
      
      // Â¶ÇÊûúÂÆåÂÖ®Â§±Ë¥•ÔºåËá≥Â∞ëËøîÂõûÊ®°ÊãüÊï∞ÊçÆ
      if (this.jobs.length === 0) {
        this.jobs = mockJobs.map(job => ({
          ...job,
          fetchedAt: new Date().toISOString(),
          isNew: false
        }));
        this.sources = ['Mock Data'];
      }

      return {
        success: false,
        error: error.message,
        jobs: this.jobs,
        sources: this.sources
      };
    } finally {
      this.isUpdating = false;
    }
  }

  // ‰ªéRemoteOKËé∑ÂèñÊï∞ÊçÆ
  async fetchFromRemoteOK() {
    try {
      const jobs = await fetchRemoteOKJobs();
      return {
        jobs: jobs.map(job => ({ ...job, dataSource: 'RemoteOK' })),
        source: 'RemoteOK'
      };
    } catch (error) {
      throw new Error(`RemoteOK API Â§±Ë¥•: ${error.message}`);
    }
  }

  // ‰ªéWeWorkRemotelyËé∑ÂèñÊï∞ÊçÆ
  async fetchFromWeWorkRemotely() {
    try {
      const jobs = await fetchWeWorkRemotelyJobs();
      return {
        jobs: jobs.map(job => ({ ...job, dataSource: 'WeWorkRemotely' })),
        source: 'WeWorkRemotely'
      };
    } catch (error) {
      throw new Error(`WeWorkRemotely API Â§±Ë¥•: ${error.message}`);
    }
  }

  // Ëé∑ÂèñÊ®°ÊãüÊï∞ÊçÆ‰Ωú‰∏∫Â§áÁî®
  async fetchMockData() {
    return {
      jobs: mockJobs.map(job => ({ 
        ...job, 
        dataSource: 'Mock Data',
        fetchedAt: new Date().toISOString()
      })),
      source: 'Mock Data'
    };
  }

  // ÂéªÈáçÂ§ÑÁêÜ
  deduplicateJobs(jobs) {
    const seen = new Map();
    const uniqueJobs = [];

    for (const job of jobs) {
      // ÂàõÂª∫ÂîØ‰∏ÄÊ†áËØÜÁ¨¶
      const key = `${job.title.toLowerCase().trim()}-${job.company.toLowerCase().trim()}`;
      
      if (!seen.has(key)) {
        seen.set(key, true);
        uniqueJobs.push(job);
      } else {
        console.log(`üîÑ ÂéªÈáç: ${job.title} @ ${job.company}`);
      }
    }

    return uniqueJobs;
  }

  // Ê£ÄÊü•ÊòØÂê¶‰∏∫Êñ∞Â∑•‰Ωú
  isNewJob(job) {
    // ÁÆÄÂçïÁöÑÊñ∞Â∑•‰ΩúÊ£ÄÊµãÔºöÂèëÂ∏ÉÊó∂Èó¥‰∏∫‰ªäÂ§©
    return job.postedDate === 'Today';
  }

  // ÊêúÁ¥¢Â∑•‰Ωú
  searchJobs(searchTerm = '', filters = {}) {
    let filteredJobs = [...this.jobs];

    // ÊêúÁ¥¢ËØçËøáÊª§
    if (searchTerm && searchTerm.trim()) {
      const term = searchTerm.toLowerCase().trim();
      filteredJobs = filteredJobs.filter(job => {
        return (
          job.title.toLowerCase().includes(term) ||
          job.company.toLowerCase().includes(term) ||
          job.description.toLowerCase().includes(term) ||
          (job.skills && job.skills.some(skill => skill.toLowerCase().includes(term)))
        );
      });
    }

    // Â∫îÁî®Á≠õÈÄâÂô®
    if (filters.jobType) {
      filteredJobs = filteredJobs.filter(job => 
        job.type && job.type.toLowerCase() === filters.jobType.toLowerCase()
      );
    }

    if (filters.team) {
      filteredJobs = filteredJobs.filter(job => 
        job.team && job.team.toLowerCase() === filters.team.toLowerCase()
      );
    }

    if (filters.salary) {
      const { min, max } = filters.salary;
      filteredJobs = filteredJobs.filter(job => 
        (!min || job.salary >= min) && (!max || job.salary <= max)
      );
    }

    // ÊéíÂ∫è
    filteredJobs = this.sortJobs(filteredJobs, filters.sortBy || 'date');

    return {
      jobs: filteredJobs,
      total: filteredJobs.length,
      sources: this.sources,
      lastUpdate: this.lastUpdate,
      hasMore: false
    };
  }

  // ÊéíÂ∫èÂ∑•‰Ωú
  sortJobs(jobs, sortBy = 'date') {
    const sortedJobs = [...jobs];
    
    switch (sortBy) {
      case 'date':
        return sortedJobs.sort((a, b) => {
          // Êñ∞Â∑•‰Ωú‰ºòÂÖà
          if (a.isNew && !b.isNew) return -1;
          if (!a.isNew && b.isNew) return 1;
          
          // ÊåâÂèëÂ∏ÉÊó•ÊúüÊéíÂ∫è
          const dateOrder = ['Today', 'Yesterday', '2 days ago', '3 days ago', '1 week ago'];
          const indexA = dateOrder.indexOf(a.postedDate);
          const indexB = dateOrder.indexOf(b.postedDate);
          
          if (indexA !== -1 && indexB !== -1) return indexA - indexB;
          if (indexA !== -1) return -1;
          if (indexB !== -1) return 1;
          
          return 0;
        });
      
      case 'salary':
        return sortedJobs.sort((a, b) => (b.salary || 0) - (a.salary || 0));
      
      case 'company':
        return sortedJobs.sort((a, b) => a.company.localeCompare(b.company));
      
      default:
        return sortedJobs;
    }
  }

  // Ê∑ªÂä†Êï∞ÊçÆÊõ¥Êñ∞ÁõëÂê¨Âô®
  addUpdateListener(callback) {
    this.listeners.add(callback);
    return () => this.listeners.delete(callback);
  }

  // ÈÄöÁü•ÊâÄÊúâÁõëÂê¨Âô®
  notifyListeners(data) {
    this.listeners.forEach(callback => {
      try {
        callback(data);
      } catch (error) {
        console.error('ÁõëÂê¨Âô®ÂõûË∞ÉÈîôËØØ:', error);
      }
    });
  }

  // Ëé∑ÂèñÁªüËÆ°‰ø°ÊÅØ
  getStats() {
    return {
      ...this.stats,
      totalJobs: this.jobs.length,
      newJobs: this.jobs.filter(job => job.isNew).length,
      sources: this.sources,
      lastUpdate: this.lastUpdate,
      isUpdating: this.isUpdating,
      updateFrequency: this.updateFrequency
    };
  }

  // ËÆæÁΩÆÊõ¥Êñ∞È¢ëÁéá
  setUpdateFrequency(minutes) {
    this.updateFrequency = minutes * 60 * 1000;
    
    // Â¶ÇÊûúÊ≠£Âú®ËøêË°åÔºåÈáçÊñ∞ÂêØÂä®ÂÆöÊó∂Âô®
    if (this.updateInterval) {
      this.stopRealTimeUpdates();
      this.startRealTimeUpdates();
    }
    
    console.log(`‚è∞ Êõ¥Êñ∞È¢ëÁéáËÆæÁΩÆ‰∏∫ ${minutes} ÂàÜÈíü`);
  }

  // Ëé∑ÂèñÂΩìÂâçÂ∑•‰ΩúÊï∞ÊçÆ
  getCurrentJobs() {
    return {
      jobs: this.jobs,
      sources: this.sources,
      lastUpdate: this.lastUpdate,
      stats: this.getStats()
    };
  }
}

// ÂàõÂª∫ÂÖ®Â±ÄÂÆû‰æã
const realTimeJobService = new RealTimeJobService();

export default realTimeJobService;
