import React from 'react';
import './JobCard.css';

const JobCard = ({ job }) => {
  const formatSalary = (salary) => {
    if (!salary) return 'Salary not specified';
    return `$${salary}k /year`;
  };

  return (
    <div className="job-card">
      <div className="job-card-left">
        <div className="company-logo">
          <img src={job.companyLogo} alt={job.company} />
        </div>
      </div>
      
      <div className="job-card-content">
        <div className="job-header">
          <h3 className="job-title">{job.title}</h3>
          <div className="job-company">{job.company}</div>
          
          <div className="job-meta">
            <div className="job-location">
              <span className="location-icon">üìç</span>
              {job.location}
            </div>
            
            <div className="job-stats">
              {job.views && (
                <div className="job-views">
                  <span className="views-icon">üëÅÔ∏è</span>
                  {job.views} views
                </div>
              )}
              
              {job.applicants && (
                <div className="job-applicants">
                  <span className="applicants-icon">üë§</span>
                  {job.applicants} applied
                </div>
              )}
            </div>
          </div>
          
          <div className="job-details">
            <div className="job-posted">
              {job.postedDate === 'Today' ? (
                <span className="today-tag">Today</span>
              ) : (
                <span>{job.postedDate}</span>
              )}
            </div>
            
            <div className="job-type">
              <span className="job-type-tag">{job.type}</span>
            </div>
          </div>
        </div>
      </div>
      
      <div className="job-card-right">
        <div className="job-actions">
          <button className="job-action-btn save-btn">
            <span className="action-icon">üîñ</span>
          </button>
          <button className="job-action-btn info-btn">
            <span className="action-icon">‚ÑπÔ∏è</span>
          </button>
        </div>
        
        <div className="job-team">
          <div className="team-label">Team</div>
          <div className="team-name">{job.team}</div>
        </div>
        
        <div className="job-salary">
          {formatSalary(job.salary)}
        </div>
      </div>
    </div>
  );
};

export default JobCard;
