import React, { useState } from 'react';
import './Sidebar.css';

const Sidebar = ({ currentPage, user, onNavigate, isOpen = true }) => {
  const [showUserMenu, setShowUserMenu] = useState(false);
  
  const navigationItems = [
    { id: 'home', label: 'Home', icon: '⌂' }
  ];

  const toggleUserMenu = () => {
    setShowUserMenu(!showUserMenu);
  };

  const handleClickOutside = () => {
    if (showUserMenu) {
      setShowUserMenu(false);
    }
  };
  
  // 处理键盘事件
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === ' ') {
      toggleUserMenu();
      e.preventDefault();
    } else if (e.key === 'Escape' && showUserMenu) {
      setShowUserMenu(false);
    }
  };

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`}>
      <div className="sidebar-header">
        <h1 className="logo">HuntJobs</h1>
      </div>
      
      <nav className="sidebar-nav">
        {navigationItems.map(item => (
          <button
            key={item.id}
            className={`nav-item ${currentPage === item.id ? 'active' : ''}`}
            onClick={() => onNavigate(item.id)}
            aria-label={item.label}
            aria-current={currentPage === item.id ? 'page' : undefined}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
            {currentPage === item.id && <span className="active-indicator"></span>}
          </button>
        ))}
      </nav>
      
      <div className="sidebar-footer">
        <div 
          className="user-profile" 
          onClick={toggleUserMenu}
          onKeyDown={handleKeyDown}
          role="button"
          tabIndex={0}
          aria-expanded={showUserMenu}
          aria-haspopup="true"
        >
          <div className="user-avatar">
            <img src={user.avatar} alt={user.name} />
            {user.isOnline && <span className="online-indicator"></span>}
          </div>
          <div className="user-info">
            <div className="user-name">{user.name}</div>
            <div className="user-role">{user.role}</div>
          </div>
          <span className="user-menu-arrow">▾</span>
        </div>
        
        {showUserMenu && (
          <div className="user-menu">
            <button className="user-menu-item">Profile</button>
            <button className="user-menu-item">Settings</button>
            <button className="user-menu-item">Help</button>
            <div className="user-menu-divider"></div>
            <button className="user-menu-item logout">Sign Out</button>
          </div>
        )}
      </div>
      
      {showUserMenu && (
        <div className="overlay" onClick={handleClickOutside}></div>
      )}
    </div>
  );
};

export default Sidebar;
