import React from 'react';
import './Sidebar.css';

const Sidebar = ({ currentPage, user, onNavigate }) => {
  const navigationItems = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'find-work', label: 'Find Work', icon: 'üîç' },
    { id: 'my-jobs', label: 'My Jobs', icon: 'üíº' },
    { id: 'my-activity', label: 'My Activity', icon: 'üìä' },
    { id: 'messages', label: 'Messages', icon: 'üí¨' },
    { id: 'reports', label: 'Reports', icon: 'üìà' }
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h1 className="logo">HuntJobs</h1>
      </div>
      
      <nav className="sidebar-nav">
        {navigationItems.map(item => (
          <button
            key={item.id}
            className={`nav-item ${currentPage === item.id ? 'active' : ''}`}
            onClick={() => onNavigate(item.id)}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
            {currentPage === item.id && <span className="active-indicator"></span>}
          </button>
        ))}
      </nav>
      
      <div className="sidebar-footer">
        <div className="user-profile">
          <div className="user-avatar">
            <img src={user.avatar} alt={user.name} />
            {user.isOnline && <span className="online-indicator"></span>}
          </div>
          <div className="user-info">
            <div className="user-name">{user.name}</div>
            <div className="user-role">{user.role}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
