import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from '../components/Header';
import JobCard from '../components/JobCard';
import JobDetailsDrawer from '../components/JobDetailsDrawer';
import { mockJobs } from '../services/mockData';
import jobService from '../services/jobService';
import './JobFeed.css';

const JobFeed = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searching, setSearching] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);
  const [dataSources, setDataSources] = useState([]);
  const [dynamicCategories, setDynamicCategories] = useState([]);
  const observer = useRef();
  const lastJobElementRef = useCallback(node => {
    if (loading || loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        loadMoreJobs();
      }
    });
    if (node) observer.current.observe(node);
  }, [loading, loadingMore, hasMore]);

  // 加载更多工作
  const loadMoreJobs = async () => {
    if (loadingMore || !hasMore) return;
    
    setLoadingMore(true);
    try {
      const nextPage = page + 1;
      const designSearchTerms = searchTerm || '';
      
      const result = await jobService.searchRemoteJobs(designSearchTerms, {
        ...activeFilters,
        category: 'design',
        page: nextPage
      });
      
      if (result.jobs.length === 0) {
        setHasMore(false);
      } else {
        // 处理新加载的工作数据
        const newJobs = filterJobs(result.jobs);
        
        if (newJobs.length === 0) {
          setHasMore(false);
        } else {
          setJobs(prevJobs => [...prevJobs, ...newJobs]);
          setFilteredJobs(prevFiltered => [...prevFiltered, ...newJobs]);
          setPage(nextPage);
          
          // 更新动态分类（基于所有已加载的工作）
          const allLoadedJobs = [...jobs, ...newJobs];
          const categories = generateDynamicCategories(allLoadedJobs);
          setDynamicCategories(categories);
        }
      }
    } catch (error) {
      console.error('Failed to load more jobs:', error);
    } finally {
      setLoadingMore(false);
    }
  };

  // 根据工作数据自动生成分类
  const generateDynamicCategories = (jobs) => {
    console.log('生成动态分类，工作数量:', jobs.length);
    
    const categoryMap = new Map();
    
    // 定义分类规则和关键词
    const categoryRules = {
      'ux-designer': {
        label: 'UX Designer',
        keywords: ['ux', 'user experience', 'ux designer', 'ux/ui', 'ui/ux', 'interaction design', 'usability', 'user research', 'experience design', 'design researcher', 'ux research'],
        count: 0
      },
      'ui-designer': {
        label: 'UI Designer',
        keywords: ['ui', 'user interface', 'ui designer', 'interface design', 'visual design'],
        count: 0
      },
      'product-designer': {
        label: 'Product Designer',
        keywords: ['product designer', 'product design', 'digital product', 'product manager'],
        count: 0
      },
      'frontend-developer': {
        label: 'Frontend Developer',
        keywords: ['frontend', 'front-end', 'front end', 'react', 'vue', 'angular', 'javascript', 'html', 'css'],
        count: 0
      },
      'backend-developer': {
        label: 'Backend Developer',
        keywords: ['backend', 'back-end', 'back end', 'api', 'server', 'node.js', 'python', 'java', 'php', 'ruby'],
        count: 0
      },
      'fullstack-developer': {
        label: 'Full Stack Developer',
        keywords: ['full stack', 'fullstack', 'full-stack'],
        count: 0
      },
      'data-scientist': {
        label: 'Data Scientist',
        keywords: ['data scientist', 'data science', 'machine learning', 'ml', 'ai', 'artificial intelligence', 'analytics', 'big data'],
        count: 0
      },
      'devops-engineer': {
        label: 'DevOps Engineer',
        keywords: ['devops', 'dev ops', 'sre', 'site reliability', 'infrastructure', 'cloud', 'aws', 'azure', 'gcp', 'kubernetes', 'docker'],
        count: 0
      },
      'mobile-developer': {
        label: 'Mobile Developer',
        keywords: ['mobile', 'ios', 'android', 'react native', 'flutter', 'swift', 'kotlin'],
        count: 0
      },
      'graphic-designer': {
        label: 'Graphic Designer',
        keywords: ['graphic designer', 'visual designer', 'brand designer', 'creative designer', 'graphic design'],
        count: 0
      },
      'marketing': {
        label: 'Marketing',
        keywords: ['marketing', 'digital marketing', 'content marketing', 'social media', 'seo', 'sem', 'growth'],
        count: 0
      },
      'project-manager': {
        label: 'Project Manager',
        keywords: ['project manager', 'program manager', 'scrum master', 'agile', 'project management'],
        count: 0
      }
    };
    
    // 分析每个工作，统计分类
    jobs.forEach(job => {
      const title = job.title.toLowerCase();
      const description = job.description ? job.description.toLowerCase() : '';
      const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
      const searchText = `${title} ${description} ${skills}`;
      
      // 检查每个分类规则
      Object.entries(categoryRules).forEach(([categoryId, categoryInfo]) => {
        const hasMatch = categoryInfo.keywords.some(keyword => 
          searchText.includes(keyword.toLowerCase())
        );
        
        if (hasMatch) {
          categoryInfo.count++;
        }
      });
    });
    
    // 只返回有工作的分类，并按数量排序
    const validCategories = Object.entries(categoryRules)
      .filter(([_, categoryInfo]) => categoryInfo.count > 0)
      .sort(([, a], [, b]) => b.count - a.count)
      .slice(0, 8) // 最多显示8个分类
      .map(([categoryId, categoryInfo]) => ({
        id: categoryId,
        label: `${categoryInfo.label} (${categoryInfo.count})`,
        count: categoryInfo.count
      }));
    
    console.log('生成的动态分类:', validCategories);
    return validCategories;
  };

  // 过滤函数：保留所有工作，不再限制为设计工作
  const filterJobs = (jobs) => {
    console.log('处理工作数据，总数:', jobs.length);
    return jobs; // 返回所有工作
  };

  useEffect(() => {
    // 获取真实的远程工作数据
    const fetchJobs = async () => {
      setLoading(true);
      setJobs([]);
      setFilteredJobs([]);
      setPage(0);
      setHasMore(true);
      
      try {
        // 使用更通用的搜索词来获取更多结果
        const designSearchTerms = searchTerm || '';
        
        // 尝试从真实API获取数据
        const result = await jobService.searchRemoteJobs(designSearchTerms, {
          ...activeFilters,
          category: 'design' // 如果API支持分类，优先获取设计类工作
        });
        
        // 先查看所有返回的工作，然后再过滤
        console.log('All jobs returned from API:', result.jobs.length);
        console.log('Sample jobs:', result.jobs.slice(0, 5).map(job => ({
          title: job.title,
          company: job.company,
          description: job.description ? job.description.substring(0, 100) + '...' : 'No description',
          skills: job.skills
        })));
        
        // 处理所有工作数据
        const allJobs = filterJobs(result.jobs);
        
        console.log(`处理了 ${allJobs.length} 个工作`);
        setJobs(allJobs);
        setFilteredJobs(allJobs);
        
        // 生成动态分类
        const categories = generateDynamicCategories(allJobs);
        setDynamicCategories(categories);
        setDataSources(result.sources || []);
        
        // 如果返回的工作数量小于预期，可能没有更多数据了
        if (result.jobs.length < 20) {
          setHasMore(false);
        }
      } catch (error) {
        console.error('Failed to fetch jobs from API, using mock data:', error);
        // 如果API失败，使用模拟数据作为后备
        const filteredMockJobs = filterJobs(mockJobs);
        setJobs(filteredMockJobs);
        setFilteredJobs(filteredMockJobs);
        
        // 生成动态分类
        const categories = generateDynamicCategories(filteredMockJobs);
        setDynamicCategories(categories);
        setDataSources(['Mock Data']);
        setHasMore(false);
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, [searchTerm, JSON.stringify(activeFilters)]);

  const handleSearch = (term) => {
    // 更新搜索词状态
    if (term !== undefined && term !== null) {
      setSearchTerm(term);
    }
    
    // 设置搜索状态为正在搜索
    setSearching(true);
    
    // 使用setTimeout模拟搜索延迟，提供更好的用户体验
    setTimeout(() => {
      // 重新应用筛选器，包括新的搜索词
      applyFiltersToJobs(activeFilters);
      setSearching(false);
    }, 300); // 300ms的延迟，提供更流畅的搜索体验
  };

  const handleFilter = (filterIds) => {
    console.log('筛选器更新:', filterIds);
    
    // 更新活动筛选器状态
    setActiveFilters(filterIds);
    
    // 立即应用筛选到当前已加载的工作
    applyFiltersToJobs(filterIds);
  };

  // 应用筛选器到工作列表
  const applyFiltersToJobs = (filterIds) => {
    let results = [...jobs];
    
    // 首先按搜索词过滤
    if (searchTerm) {
      results = results.filter(job => 
        job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (job.description && job.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }
    
    // 然后按职位类型筛选
    if (filterIds && filterIds.length > 0) {
      results = results.filter(job => {
        const title = job.title.toLowerCase();
        const description = job.description ? job.description.toLowerCase() : '';
        const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
        const searchText = `${title} ${description} ${skills}`;
        
        // 检查每个激活的筛选器
        return filterIds.some(filterId => {
          // 从动态分类中找到对应的分类规则
          const categoryRules = {
            'ux-designer': ['ux', 'user experience', 'ux designer', 'ux/ui', 'ui/ux', 'interaction design', 'usability', 'user research', 'experience design', 'design researcher', 'ux research'],
            'ui-designer': ['ui', 'user interface', 'ui designer', 'interface design', 'visual design'],
            'product-designer': ['product designer', 'product design', 'digital product', 'product manager'],
            'frontend-developer': ['frontend', 'front-end', 'front end', 'react', 'vue', 'angular', 'javascript', 'html', 'css'],
            'backend-developer': ['backend', 'back-end', 'back end', 'api', 'server', 'node.js', 'python', 'java', 'php', 'ruby'],
            'fullstack-developer': ['full stack', 'fullstack', 'full-stack'],
            'data-scientist': ['data scientist', 'data science', 'machine learning', 'ml', 'ai', 'artificial intelligence', 'analytics', 'big data'],
            'devops-engineer': ['devops', 'dev ops', 'sre', 'site reliability', 'infrastructure', 'cloud', 'aws', 'azure', 'gcp', 'kubernetes', 'docker'],
            'mobile-developer': ['mobile', 'ios', 'android', 'react native', 'flutter', 'swift', 'kotlin'],
            'graphic-designer': ['graphic designer', 'visual designer', 'brand designer', 'creative designer', 'graphic design'],
            'marketing': ['marketing', 'digital marketing', 'content marketing', 'social media', 'seo', 'sem', 'growth'],
            'project-manager': ['project manager', 'program manager', 'scrum master', 'agile', 'project management']
          };
          
          const keywords = categoryRules[filterId] || [];
          return keywords.some(keyword => searchText.includes(keyword.toLowerCase()));
        });
      });
    }
    
    console.log(`筛选结果: ${results.length} 个工作 (原始: ${jobs.length}, 筛选器: ${filterIds})`);
    setFilteredJobs(results);
  };
  


  // 处理工作卡片点击
  const handleJobSelect = (job) => {
    setSelectedJob(job);
    setIsDrawerOpen(true);
  };
  
  // 处理关闭抽屉
  const handleCloseDrawer = () => {
    setIsDrawerOpen(false);
  };
  
  // 处理申请工作
  const handleApplyJob = (jobId) => {
    console.log(`Applying for job: ${jobId}`);
    
    // 直接使用selectedJob，而不是从jobs数组中查找
    const job = selectedJob;
    
    if (!job) {
      console.error('No selected job found');
      alert('无法找到选中的工作');
      return;
    }
    
    // 详细记录工作对象，帮助调试
    console.log('Selected job details:', {
      id: job.id,
      title: job.title,
      company: job.company,
      source: job.source,
      sourceUrl: job.sourceUrl,
      sourceId: job.sourceId
    });
    
    // 创建一个隐藏的a标签来打开链接，这样可以避免弹出窗口被阻止
    const openLink = (url) => {
      console.log(`Attempting to open URL: ${url}`);
      
      // 创建一个隐藏的a标签
      const link = document.createElement('a');
      link.href = url;
      link.target = '_blank';
      link.rel = 'noopener noreferrer';
      
      // 添加到文档中并触发点击
      document.body.appendChild(link);
      link.click();
      
      // 清理
      setTimeout(() => {
        document.body.removeChild(link);
      }, 100);
    };
    
    let finalUrl = '';
    
    if (job.sourceUrl) {
      // 如果有源URL，使用它
      console.log(`Using source URL: ${job.sourceUrl}`);
      finalUrl = job.sourceUrl;
    } else if (job.source) {
      // 如果没有源URL但有来源信息，尝试构建URL
      switch (job.source) {
        case 'LinkedIn':
          finalUrl = `https://www.linkedin.com/jobs/view/${job.sourceId || job.id}`;
          break;
        case 'WeWorkRemotely':
          // 尝试使用更可能正确的WeWorkRemotely URL格式
          if (job.company && job.title) {
            // 将公司名和职位名转换为URL友好的格式
            const companySlug = job.company.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            const titleSlug = job.title.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            finalUrl = `https://weworkremotely.com/remote-jobs/${companySlug}-${titleSlug}`;
          } else {
            finalUrl = `https://weworkremotely.com/remote-jobs/${job.sourceId || job.id}`;
          }
          break;
        case 'RemoteOK':
          finalUrl = `https://remoteok.io/l/${job.sourceId || job.id}`;
          break;
        default:
          // 如果无法确定来源，尝试使用通用搜索
          finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      }
      console.log(`Constructed URL: ${finalUrl}`);
    } else {
      // 如果没有任何链接信息，显示提示并使用Google搜索
      console.error('No source URL or source information found for job:', job);
      finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      alert('无法找到精确的工作链接，将使用Google搜索');
    }
    
    // 尝试打开链接
    if (finalUrl) {
      openLink(finalUrl);
    } else {
      alert('无法生成有效的工作链接');
    }
  };

  return (
    <div className={`job-feed ${isDrawerOpen ? 'drawer-open' : ''}`}>
      <Header 
        onSearch={(term) => handleSearch(term)} 
        onFilter={handleFilter}
        dynamicCategories={dynamicCategories}
      />
      
      <div className="job-list-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading jobs...</p>
          </div>
        ) : searching ? (
          <div className="searching-container">
            <div className="loading-spinner small"></div>
            <p>Searching...</p>
          </div>
        ) : (
          <>
            {filteredJobs.length > 0 ? (
              <>
                <div className="results-count">
                  找到 <span className="count-highlight">{filteredJobs.length}</span> 个工作机会
                </div>
                <div className="data-sources">
                  数据来源: {dataSources.join(', ')}
                </div>
                <div className="job-list">
                  {filteredJobs.map((job, index) => {
                    if (filteredJobs.length === index + 1) {
                      return (
                        <div ref={lastJobElementRef} key={job.id}>
                          <JobCard 
                            job={job} 
                            onSelect={() => handleJobSelect(job)}
                          />
                        </div>
                      );
                    } else {
                      return (
                        <JobCard 
                          key={job.id} 
                          job={job} 
                          onSelect={() => handleJobSelect(job)}
                        />
                      );
                    }
                  })}
                  {loadingMore && (
                    <div className="loading-more">
                      <div className="loading-spinner small"></div>
                      <p>加载更多工作...</p>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="no-jobs-found">
                <h3>No jobs found</h3>
                <p>Try adjusting your search filters</p>
              </div>
            )}
          </>
        )}
      </div>
      
      <JobDetailsDrawer 
        job={selectedJob}
        isOpen={isDrawerOpen}
        onClose={handleCloseDrawer}
        onApply={handleApplyJob}
      />
    </div>
  );
};

export default JobFeed;
