import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import JobFeed from './pages/JobFeed';
import MarkPage from './pages/MarkPage';
import './styles/App.css';
import './styles/placeholder.css';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [isMobile, setIsMobile] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  


  // Ê£ÄÊµãÂ±èÂπïÂ∞∫ÂØ∏ÂèòÂåñ
  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    
    // ÂàùÂßãÊ£ÄÊü•
    checkIfMobile();
    
    // ÁõëÂê¨Á™óÂè£Â§ßÂ∞èÂèòÂåñ
    window.addEventListener('resize', checkIfMobile);
    
    // Ê∏ÖÁêÜÁõëÂê¨Âô®
    return () => window.removeEventListener('resize', checkIfMobile);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const handleNavigate = (page) => {
    setCurrentPage(page);
    if (isMobile) {
      setSidebarOpen(false);
    }
  };

  // Ê†πÊçÆÂΩìÂâçÈ°µÈù¢ËøîÂõûÂØπÂ∫îÁöÑÁªÑ‰ª∂
  const getPageComponent = () => {
    switch (currentPage) {
      case 'home':
        return <JobFeed />;
      case 'mark':
        return <MarkPage />;
      case 'find-work':
        return <JobFeed />;
      case 'my-jobs':
        return <JobFeed filter="my-jobs" />;
      case 'my-activity':
        return <JobFeed filter="my-activity" />;
      case 'messages':
        return (
          <div className="placeholder-page">
            <div className="placeholder-icon">‚úâÔ∏è</div>
            <h2>Messages</h2>
            <p>This feature is coming soon. You will be able to communicate with employers and manage your conversations here.</p>
          </div>
        );
      case 'reports':
        return (
          <div className="placeholder-page">
            <div className="placeholder-icon">üìä</div>
            <h2>Reports</h2>
            <p>This feature is coming soon. You will be able to view analytics and reports about your job search activities here.</p>
          </div>
        );
      default:
        return <JobFeed />;
    }
  };

  return (
    <Router>
      <div className="app">
        {isMobile && (
          <button className="menu-toggle" onClick={toggleSidebar} aria-label="Toggle menu">
            ‚ò∞
          </button>
        )}
        
        <Sidebar 
          currentPage={currentPage}
          onNavigate={handleNavigate}
          isOpen={isMobile ? sidebarOpen : true}
        />
        
        <main className="main-content">
          {getPageComponent()}
        </main>
      </div>
    </Router>
  );
}

export default App;
