import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from '../components/Header';
import JobCard from '../components/JobCard';
import JobDetailsDrawer from '../components/JobDetailsDrawer';
import { mockJobs } from '../services/mockData';
import jobService from '../services/jobService';
import './JobFeed.css';

const JobFeed = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searching, setSearching] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [activeFilters, setActiveFilters] = useState({});
  const [dataSources, setDataSources] = useState([]);
  const observer = useRef();
  const lastJobElementRef = useCallback(node => {
    if (loading || loadingMore) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        loadMoreJobs();
      }
    });
    if (node) observer.current.observe(node);
  }, [loading, loadingMore, hasMore]);

  // 加载更多工作
  const loadMoreJobs = async () => {
    if (loadingMore || !hasMore) return;
    
    setLoadingMore(true);
    try {
      const nextPage = page + 1;
      const result = await jobService.searchRemoteJobs(searchTerm, {
        ...activeFilters,
        page: nextPage
      });
      
      if (result.jobs.length === 0) {
        setHasMore(false);
      } else {
        setJobs(prevJobs => [...prevJobs, ...result.jobs]);
        setFilteredJobs(prevFiltered => [...prevFiltered, ...result.jobs]);
        setPage(nextPage);
      }
    } catch (error) {
      console.error('Failed to load more jobs:', error);
    } finally {
      setLoadingMore(false);
    }
  };

  // 过滤函数：只保留UX designer和Product designer的工作
  const filterDesignJobs = (jobs) => {
    return jobs.filter(job => {
      const title = job.title.toLowerCase();
      const description = job.description ? job.description.toLowerCase() : '';
      const skills = job.skills ? job.skills.join(' ').toLowerCase() : '';
      
      // 检查是否包含UX designer相关关键词
      const isUXDesigner = 
        title.includes('ux') || 
        title.includes('user experience') ||
        title.includes('ux designer') ||
        title.includes('ux/ui') ||
        description.includes('user experience') ||
        skills.includes('ux');
      
      // 检查是否包含Product designer相关关键词
      const isProductDesigner = 
        title.includes('product designer') ||
        title.includes('product design') ||
        (title.includes('product') && title.includes('design')) ||
        description.includes('product design') ||
        skills.includes('product design');
      
      return isUXDesigner || isProductDesigner;
    });
  };

  useEffect(() => {
    // 获取真实的远程工作数据
    const fetchJobs = async () => {
      setLoading(true);
      setJobs([]);
      setFilteredJobs([]);
      setPage(0);
      setHasMore(true);
      
      try {
        // 使用设计相关的搜索词来获取更相关的结果
        const designSearchTerms = searchTerm || 'UX designer OR product designer OR user experience OR product design';
        
        // 尝试从真实API获取数据
        const result = await jobService.searchRemoteJobs(designSearchTerms, {
          ...activeFilters,
          category: 'design' // 如果API支持分类，优先获取设计类工作
        });
        
        // 过滤出只包含UX designer和Product designer的工作
        const designJobs = filterDesignJobs(result.jobs);
        
        setJobs(designJobs);
        setFilteredJobs(designJobs);
        setDataSources(result.sources || []);
        
        // 如果返回的工作数量小于预期，可能没有更多数据了
        if (designJobs.length < 20) {
          setHasMore(false);
        }
      } catch (error) {
        console.error('Failed to fetch jobs from API, using mock data:', error);
        // 如果API失败，使用模拟数据作为后备，但仍然过滤设计工作
        const filteredMockJobs = filterDesignJobs(mockJobs);
        setJobs(filteredMockJobs);
        setFilteredJobs(filteredMockJobs);
        setDataSources(['Mock Data']);
        setHasMore(false);
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, [searchTerm, JSON.stringify(activeFilters)]);

  const handleSearch = (term, activeFilters) => {
    // 更新搜索词状态
    if (term !== undefined && term !== null) {
      setSearchTerm(term);
    }
    
    // 设置搜索状态为正在搜索
    setSearching(true);
    
    // 使用setTimeout模拟搜索延迟，提供更好的用户体验
    setTimeout(() => {
      let results = [...jobs];
      const searchValue = term !== undefined && term !== null ? term : searchTerm;
      
      // 按搜索词过滤
      if (searchValue) {
        results = results.filter(job => 
          job.title.toLowerCase().includes(searchValue.toLowerCase()) ||
          job.company.toLowerCase().includes(searchValue.toLowerCase()) ||
          job.location.toLowerCase().includes(searchValue.toLowerCase())
        );
      }
      
      // 按职位类型过滤
      if (activeFilters && activeFilters.length > 0) {
        results = results.filter(job => {
          // 这里简化处理，实际应用中需要更复杂的匹配逻辑
          if (activeFilters.includes('ux-designer') && job.title.toLowerCase().includes('ux')) {
            return true;
          }
          if (activeFilters.includes('ui-designer') && job.title.toLowerCase().includes('ui')) {
            return true;
          }
          if (activeFilters.includes('product-designer') && job.title.toLowerCase().includes('product')) {
            return true;
          }
          if (activeFilters.includes('visual-identity') && 
              (job.title.toLowerCase().includes('visual') || job.title.toLowerCase().includes('graphic'))) {
            return true;
          }
          return false;
        });
      }
      
      setFilteredJobs(results);
      setSearching(false);
    }, 300); // 300ms的延迟，提供更流畅的搜索体验
  };

  const handleFilter = (filters) => {
    // 更新活动筛选条件
    setActiveFilters(prevFilters => ({
      ...prevFilters,
      ...filters
    }));
    
    // 重置页码和加载状态，触发useEffect重新获取数据
    setPage(0);
    setHasMore(true);
  };
  
  const handleSort = (sortOption) => {
    // 更新排序选项
    setActiveFilters(prevFilters => ({
      ...prevFilters,
      sort: sortOption
    }));
    
    // 本地排序当前已加载的工作
    let sorted = [...filteredJobs];
    
    switch (sortOption) {
      case 'salary':
        sorted.sort((a, b) => b.salary - a.salary);
        break;
      case 'newest':
        sorted.sort((a, b) => {
          if (a.postedDate === 'Today') return -1;
          if (b.postedDate === 'Today') return 1;
          if (a.postedDate === 'Yesterday' && b.postedDate !== 'Today') return -1;
          if (b.postedDate === 'Yesterday' && a.postedDate !== 'Today') return 1;
          
          // 比较天数
          const getNumericDays = (dateStr) => {
            if (dateStr.includes('days ago')) {
              return parseInt(dateStr.split(' ')[0], 10);
            }
            if (dateStr.includes('weeks ago')) {
              return parseInt(dateStr.split(' ')[0], 10) * 7;
            }
            if (dateStr.includes('months ago')) {
              return parseInt(dateStr.split(' ')[0], 10) * 30;
            }
            return 0;
          };
          
          return getNumericDays(a.postedDate) - getNumericDays(b.postedDate);
        });
        break;
      case 'relevant':
        // 这里可以实现更复杂的相关性排序逻辑
        break;
      default:
        break;
    }
    
    setFilteredJobs(sorted);
  };

  // 处理工作卡片点击
  const handleJobSelect = (job) => {
    setSelectedJob(job);
    setIsDrawerOpen(true);
  };
  
  // 处理关闭抽屉
  const handleCloseDrawer = () => {
    setIsDrawerOpen(false);
  };
  
  // 处理申请工作
  const handleApplyJob = (jobId) => {
    console.log(`Applying for job: ${jobId}`);
    
    // 直接使用selectedJob，而不是从jobs数组中查找
    const job = selectedJob;
    
    if (!job) {
      console.error('No selected job found');
      alert('无法找到选中的工作');
      return;
    }
    
    // 详细记录工作对象，帮助调试
    console.log('Selected job details:', {
      id: job.id,
      title: job.title,
      company: job.company,
      source: job.source,
      sourceUrl: job.sourceUrl,
      sourceId: job.sourceId
    });
    
    // 创建一个隐藏的a标签来打开链接，这样可以避免弹出窗口被阻止
    const openLink = (url) => {
      console.log(`Attempting to open URL: ${url}`);
      
      // 创建一个隐藏的a标签
      const link = document.createElement('a');
      link.href = url;
      link.target = '_blank';
      link.rel = 'noopener noreferrer';
      
      // 添加到文档中并触发点击
      document.body.appendChild(link);
      link.click();
      
      // 清理
      setTimeout(() => {
        document.body.removeChild(link);
      }, 100);
    };
    
    let finalUrl = '';
    
    if (job.sourceUrl) {
      // 如果有源URL，使用它
      console.log(`Using source URL: ${job.sourceUrl}`);
      finalUrl = job.sourceUrl;
    } else if (job.source) {
      // 如果没有源URL但有来源信息，尝试构建URL
      switch (job.source) {
        case 'LinkedIn':
          finalUrl = `https://www.linkedin.com/jobs/view/${job.sourceId || job.id}`;
          break;
        case 'WeWorkRemotely':
          // 尝试使用更可能正确的WeWorkRemotely URL格式
          if (job.company && job.title) {
            // 将公司名和职位名转换为URL友好的格式
            const companySlug = job.company.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            const titleSlug = job.title.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            finalUrl = `https://weworkremotely.com/remote-jobs/${companySlug}-${titleSlug}`;
          } else {
            finalUrl = `https://weworkremotely.com/remote-jobs/${job.sourceId || job.id}`;
          }
          break;
        case 'RemoteOK':
          finalUrl = `https://remoteok.io/l/${job.sourceId || job.id}`;
          break;
        default:
          // 如果无法确定来源，尝试使用通用搜索
          finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      }
      console.log(`Constructed URL: ${finalUrl}`);
    } else {
      // 如果没有任何链接信息，显示提示并使用Google搜索
      console.error('No source URL or source information found for job:', job);
      finalUrl = `https://www.google.com/search?q=${encodeURIComponent(`${job.title} ${job.company} job`)}`;
      alert('无法找到精确的工作链接，将使用Google搜索');
    }
    
    // 尝试打开链接
    if (finalUrl) {
      openLink(finalUrl);
    } else {
      alert('无法生成有效的工作链接');
    }
  };

  return (
    <div className={`job-feed ${isDrawerOpen ? 'drawer-open' : ''}`}>
      <Header 
        onSearch={(term) => handleSearch(term)} 
        onFilter={handleFilter}
        onSort={handleSort}
      />
      
      <div className="job-list-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading jobs...</p>
          </div>
        ) : searching ? (
          <div className="searching-container">
            <div className="loading-spinner small"></div>
            <p>Searching...</p>
          </div>
        ) : (
          <>
            {filteredJobs.length > 0 ? (
              <>
                <div className="results-count">
                  找到 <span className="count-highlight">{filteredJobs.length}</span> 个工作机会
                </div>
                <div className="data-sources">
                  数据来源: {dataSources.join(', ')}
                </div>
                <div className="job-list">
                  {filteredJobs.map((job, index) => {
                    if (filteredJobs.length === index + 1) {
                      return (
                        <div ref={lastJobElementRef} key={job.id}>
                          <JobCard 
                            job={job} 
                            onSelect={() => handleJobSelect(job)}
                          />
                        </div>
                      );
                    } else {
                      return (
                        <JobCard 
                          key={job.id} 
                          job={job} 
                          onSelect={() => handleJobSelect(job)}
                        />
                      );
                    }
                  })}
                  {loadingMore && (
                    <div className="loading-more">
                      <div className="loading-spinner small"></div>
                      <p>加载更多工作...</p>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="no-jobs-found">
                <h3>No jobs found</h3>
                <p>Try adjusting your search filters</p>
              </div>
            )}
          </>
        )}
      </div>
      
      <JobDetailsDrawer 
        job={selectedJob}
        isOpen={isDrawerOpen}
        onClose={handleCloseDrawer}
        onApply={handleApplyJob}
      />
    </div>
  );
};

export default JobFeed;
